INSERT INTO full_description (film_plot, running_time, country, oskar) VALUES
  ('Бухгалтер Энди Дюфрейн обвинён в убийстве собственной жены и её любовника. Оказавшись в тюрьме под названием Шоушенк, он сталкивается с жестокостью и беззаконием, царящими по обе стороны решётки. Каждый, кто попадает в эти стены, становится их рабом до конца жизни. Но Энди, обладающий живым умом и доброй душой, находит подход как к заключённым, так и к охранникам, добиваясь их особого к себе расположения.', 142, 'США', 0),
  ('Глава семьи, Дон Вито Корлеоне, выдаёт замуж свою дочь. В это время со Второй мировой войны возвращается его любимый сын Майкл. Майкл, герой войны, гордость семьи, не выражает желания заняться жестоким семейным бизнесом. Дон Корлеоне ведёт дела по старым правилам, но наступают иные времена, и появляются люди, желающие изменить сложившиеся порядки. На Дона Корлеоне совершается покушение.', 175, 'США, Италия', 1),
  ('В центре драмы представители нового поколения гангстерского клана — дона Корлеоне и его сына, для которых не существует моральных преград на пути достижения поставленных целей. Они превращают мафию, построенную по патриархальным, еще сицилийским законам, в весьма прагматичную, жесткую корпорацию, плавно интегрирующуюся в большой бизнес Америки.', 202, 'США', 1),
  ('Бэтмен поднимает ставки в войне с криминалом. С помощью лейтенанта Джима Гордона и прокурора Харви Дента он намерен очистить улицы от преступности, отравляющей город. Сотрудничество оказывается эффективным, но скоро они обнаружат себя посреди хаоса, развязанного восходящим криминальным гением, известным испуганным горожанам под именем Джокер.', 152, 'США, Великобритания', 0),
  ('Юношу обвиняют в убийстве собственного отца, ему грозит электрический стул. Двенадцать присяжных собираются чтобы вынести вердикт: виновен или нет. С начала заседания почти все склонились к тому, что виновен, и лишь только один из двенадцати позволил себе усомниться. Счет голосов присяжных по принципу «виновен — невиновен» был 11:1. К концу собрания мнения судей кардинально изменились…', 96, 'США', 0),
  ('Фильм рассказывает реальную историю загадочного Оскара Шиндлера, члена нацистской партии, преуспевающего фабриканта, спасшего во время Второй мировой войны почти 1200 евреев.', 195, 'США', 1),
  ('Двое бандитов Винсент Вега и Джулс Винфилд ведут философские беседы в перерывах между разборками и решением проблем с должниками криминального босса Марселласа Уоллеса. В первой истории Винсент проводит незабываемый вечер с женой Марселласа Мией. Во второй рассказывается о боксёре Бутче Кулидже, купленном Уоллесом, чтобы сдать бой. В третьей истории Винсент и Джулс по нелепой случайности попадают в неприятности.', 154, 'США', 0),
  ('Последняя часть трилогии о Кольце Всевластия и о героях, взявших на себя бремя спасения Средиземья. Повелитель сил Тьмы Саурон направляет свои бесчисленные рати под стены Минас-Тирита, крепости Последней Надежды. Он предвкушает близкую победу, но именно это и мешает ему заметить две крохотные фигурки — хоббитов, приближающихся к Роковой Горе, где им предстоит уничтожить Кольцо Всевластия. Улыбнется ли им счастье?', 201, 'Новая Зеландия, США', 1),
  ('В разгар гражданской войны таинственный стрелок скитается по просторам Дикого Запада. У него нет ни дома, ни друзей, ни компаньонов, пока он не встречает двоих незнакомцев, таких же безжалостных и циничных. По воле судьбы трое мужчин вынуждены объединить свои усилия в поисках украденного золота. Но совместная работа — не самое подходящее занятие для таких отъявленных бандитов, как они. Компаньоны вскоре понимают, что в их дерзком и опасном путешествии по разоренной войной стране самое важное — никому не доверять и держать пистолет наготове, если хочешь остаться в живых.', 161, 'США, Италия, Испания', 0),
  ('Сотрудник страховой компании страдает хронической бессонницей и отчаянно пытается вырваться из мучительно скучной жизни. Однажды в очередной командировке он встречает некоего Тайлера Дёрдена — харизматического торговца мылом с извращенной философией. Тайлер уверен, что самосовершенствование — удел слабых, а единственное, ради чего стоит жить — саморазрушение. Проходит немного времени, и вот уже новые друзья лупят друг друга почем зря на стоянке перед баром, и очищающий мордобой доставляет им высшее блаженство. Приобщая других мужчин к простым радостям физической жестокости, они основывают тайный Бойцовский клуб, который начинает пользоваться невероятной популярностью.', 139, 'США', 0),
  ('Сказания о Средиземье — это хроника Великой войны за Кольцо, войны, длившейся не одну тысячу лет. Тот, кто владел Кольцом, получал власть над всеми живыми тварями, но был обязан служить злу. Тихая деревня, где живут хоббиты. Волшебник Гэндальф, придя на 111-й день рождения к своему старому другу Бильбо Бэггинсу, начинает вести разговор о кольце, которое Бильбо нашел много лет назад. Это кольцо принадлежало когда-то темному властителю Средиземья Саурону, и оно дает большую власть своему обладателю. Теперь Саурон хочет вернуть себе власть над Средиземьем. Бильбо отдает найденное кольцо своему племяннику Фродо, который пытается научиться справляться с тем страшным могуществом, которое дает ему кольцо…', 178, 'Новая Зеландия, США', 0),
  ('От лица главного героя Форреста Гампа, слабоумного безобидного человека с благородным и открытым сердцем, рассказывается история его необыкновенной жизни. Фантастическим образом превращается он в известного футболиста, героя войны, преуспевающего бизнесмена. Он становится миллиардером, но остается таким же бесхитростным, глупым и добрым. Форреста ждет постоянный успех во всем, а он любит девочку, с которой дружил в детстве, но взаимность приходит слишком поздно.', 142, 'США', 1),
  ('Борьба за Галактику обостряется в пятом эпизоде космической саги. Войска Императора начинают массированную атаку на повстанцев и их союзников. Хан Соло и принцесса Лейя укрываются в Заоблачном Городе, в котором их и захватывает Дарт Вейдер, в то время как Люк Скайуокер находится на таинственной планете джунглей Дагоба. Там Мастер - джедай Йода обучает молодого рыцаря навыкам обретения Силы. Люк даже не предполагает, как скоро ему придется воспользоваться знаниями старого Мастера: впереди битва с превосходящими силами Императора и смертельный поединок с Дартом Вейдером.', 124, 'США', 0),
  ('Кобб – талантливый вор, лучший из лучших в опасном искусстве извлечения: он крадет ценные секреты из глубин подсознания во время сна, когда человеческий разум наиболее уязвим. Редкие способности Кобба сделали его ценным игроком в привычном к предательству мире промышленного шпионажа, но они же превратили его в извечного беглеца и лишили всего, что он когда-либо любил. И вот у Кобба появляется шанс исправить ошибки. Его последнее дело может вернуть все назад, но для этого ему нужно совершить невозможное – инициацию. Вместо идеальной кражи Кобб и его команда спецов должны будут провернуть обратное. Теперь их задача – не украсть идею, а внедрить ее. Если у них получится, это и станет идеальным преступлением...', 148, 'США, Великобритания', 0),
  ('Братство распалось, но Кольцо Всевластья должно быть уничтожено. Фродо и Сэм вынуждены доверить свои жизни Голлуму, который взялся провести их к вратам Мордора. Громадная Армия Сарумана приближается: члены братства и их союзники готовы принять бой. Битва за Средиземье продолжается.', 179, 'США, Новая Зеландия', 0),
  ('Сымитировав помешательство в надежде избежать тюремного заключения, Рэндл Патрик МакМерфи попадает в психиатрическую клинику, где почти безраздельным хозяином является жестокосердная сестра Милдред Рэтчед. МакМерфи поражается тому, что прочие пациенты смирились с существующим положением вещей, а некоторые – даже сознательно пришли в лечебницу, прячась от пугающего внешнего мира. И решается на бунт. В одиночку.', 133, 'США', 1),
  ('История о Генри Хилле — начинающем гангстере, занимающемся грабежами вместе с подельниками Джими Конвеем и Томми Де Вито, которые с легкостью убивают любого, кто встаёт у них на пути.', 146, 'США', 0),
  ('Жизнь Томаса Андерсона разделена на две части: днём он - самый обычный офисный работник, получающий нагоняи от начальства, а ночью превращается в хакера по имени Нео, и нет места в сети, куда он не смог бы дотянуться. Но однажды всё меняется — герой, сам того не желая, узнаёт страшную правду: всё, что его окружает — не более, чем иллюзия, Матрица, а люди — всего лишь источник питания для искусственного интеллекта, поработившего человечество. И только Нео под силу изменить расстановку сил в этом чужом и страшном мире.', 136, 'США, Австралия', 0),
  ('Япония, XVI век. В стране полыхает гражданская война, повсюду орудуют банды разбойников и мародеров. Бедные крестьяне нанимают для защиты деревни семерых самураев, которые немного сплачивают раздробленных и малодушных селян в процессе подготовки и укрепления деревни.', 203, 'Япония', 0),
  ('Татуин. Планета-пустыня. Уже постаревший рыцарь Джедай Оби Ван Кеноби спасает молодого Люка Скайуокера, когда тот пытается отыскать пропавшего дроида. С этого момента Люк осознает свое истинное назначение: он один из рыцарей Джедай. В то время как гражданская война охватила галактику, а войска повстанцев ведут бои против сил злого Императора, к Люку и Оби Вану присоединяется отчаянный пилот-наемник Хан Соло, и в сопровождении двух дроидов, R2D2 и C-3PO, этот необычный отряд отправляется на поиски предводителя повстанцев – принцессы Леи. Героям предстоит отчаянная схватка с устрашающим Дартом Вейдером – правой рукой Императора и его секретным оружием – «Звездой Смерти».', 121, 'США', 0),
  ('Фильм охватывает события, происходящие на протяжении тридцати лет в так называемом «Городе Бога» — трущобах в бразильском городе Рио-де-Жанейро. Главные герои фильма — парень по кличке «Ракета», балансирующий между честной жизнью и мелкими правонарушениями и его знакомый Дадинью, который с восьми лет начал карьеру гангстера.', 130, 'Бразилия, Франция', 0),
  ('Детектив Уильям Сомерсет — ветеран уголовного розыска, мечтающий уйти на пенсию и уехать подальше от города и грешных обитателей. За 7 дней до пенсии на Сомерсета сваливаются две неприятности: молодой напарник Миллс и особо изощренное убийство. Острый ум опытного сыщика сразу определяет, что за этим преступлением, скорее всего, последуют другие. Новости подтверждают его догадку. Поняв, что убийца наказывает свои жертвы за совершенные ими смертные грехи, детектив встает перед выбором: вернуться к работе либо уйти и передать дело своему менее опытному напарнику?', 127, 'США', 0),
  ('Психопат похищает и убивает молодых женщин по всему Среднему Западу Америки. ФБР, уверенное в том, что все преступления совершены одним и тем же человеком, поручает агенту Клариссе Старлинг встретиться с заключенным-маньяком, который мог бы объяснить следствию психологические мотивы серийного убийцы и тем самым вывести на его след. Заключенный, доктор психиатрии Ганнибал Лектер, отбывает наказание за убийства и каннибализм. Он согласен помочь Клариссе лишь в том случае, если она попотчует его больное воображение подробностями своей сложной личной жизни. Такие двусмысленные отношения не только порождают в душе Клариссы внутренний конфликт, но и сталкивают ее лицом к лицу с помешанным до гениальности убийцей...', 118, 'США', 1),
  ('Джордж Бейли, владелец кредитной компании в выдуманном американском городке Бедфорд Фоллс, честный, отзывчивый, любящий муж и отец, подавлен свалившимися на него невзгодами и подумывает о самоубийстве. Очевидно, что человек, отказывающийся от самого большого дара Бога – жизни – нуждается в помощи ангела-хранителя. И Небеса отправляют ему на выручку Кларенса, единственного свободного на тот момент Ангела Второго Класса, приятного, доброго, но неопытного, еще даже не заслужившего крылья. Если он сделает свою работу хорошо и сможет отговорить Джорджа от смертного греха, он получит крылья. А времени у него почти не осталось… Кларенс находит единственно правильное решение – показать Джорджу мир, в котором тот не существует.', 125, 'США', 0),
  ('Во время II Мировой войны в Италии в концлагерь были отправлены евреи, отец и его маленький сын. Жена, итальянка, добровольно последовала вслед за ними. В лагере отец сказал сыну, что все происходящее вокруг является очень большой игрой за приз в настоящий танк, который достанется тому мальчику, который сможет не попасться на глаза надзирателям. Он сделал все, чтобы сын поверил в игру и остался жив, прячась в бараке.', 116, 'Италия', 0),
  ('Обычно, если есть преступление, есть мотив. Обычно, если проводится опознание, по крайней мере есть один подозреваемый. Но это не было обычным преступлением… Пятеро преступников столкнулись в одном необычном месте и решили прокрутить одно дельце. Но кто-то более сильный и могущественный, кто-то, чье имя пугает всех преступников мира, хочет, чтобы они поработали на него… Это не было обычным преступлением, и они — не просто подозрительные лица…', 106, 'США, Германия', 0),
  ('Профессиональный убийца Леон, не знающий пощады и жалости, знакомится со своей очаровательной соседкой Матильдой, семью которой расстреливают полицейские, замешанные в торговле наркотиками. Благодаря этому знакомству он впервые испытывает чувство любви, но…', 110, 'Франция, США', 0),
  ('Капитан Джон Миллер получает тяжелое задание. Вместе с отрядом из восьми человек Миллер должен отправиться в тыл врага на поиски рядового Джеймса Райана, три родных брата которого почти одновременно погибли на полях сражений. Командование приняло решение демобилизовать Райана и отправить его на родину к безутешной матери. Но для того, чтобы найти и спасти солдата, крошечному отряду придется пройти через все круги ада…', 169, 'США', 0),
  ('Тихиро с мамой и папой переезжают в новый дом. Заблудившись по дороге, они оказываются в странном пустынном городе, где их ждет великолепный пир. Родители с жадностью набрасываются на еду и к ужасу девочки превращаются в свиней, став пленниками злой колдуньи Юбабы. Теперь, оказавшись одна среди волшебных существ и загадочных видений, Тихиро должна придумать, как избавить своих родителей от чар коварной старухи.', 124, 'Япония', 1),
  ('Лидер местной банды скинхедов Дерек Виньярд прочно удерживает авторитет в своём районе. Убеждённый в своей правоте, он беспощадно расправляется с теми, кто имеет не белый цвет кожи. Независимость и смелость Дерека вызывают восхищение у его младшего брата Дэнни, который уже тоже сделал свой выбор. Но зверское убийство двух чернокожих парней, совершённое Дереком, разделяет дороги братьев: Дерек оказывается в тюрьме, где существует свой расклад сил, а Дэнни на свободе успешно продолжает дело брата. До тех пор, пока их пути не пересеклись вновь…', 119, 'США', 0),
  ('Молодая вдова отказывается продать ферму дельцу, задумавшему проложить по этой земле железную дорогу, и тогда он заказывает ее убийство, нанимая лучшего на Диком Западе стрелка. На защиту красавицы встают известный бандит Шайен и таинственный бродяга. Трое сильных мужчин сойдутся в смертельном хороводе, выхода из которого может и не быть.', 165, 'Италия, США', 1),
  ('Когда засуха, пыльные бури и вымирание растений приводят человечество к продовольственному кризису, коллектив исследователей и учёных отправляется сквозь червоточину (которая предположительно соединяет области пространства-времени через большое расстояние) в путешествие, чтобы превзойти прежние ограничения для космических путешествий человека и найти планету с подходящими для человечества условиями.', 169, 'США, Великобритания', 0),
  ('Мэрион Крэйн недовольна романом с разведенным мужчиной, который уделяет ей внимание только наездами. Поэтому девушка крадет на работе крупную сумму и в спешке бежит из города. В мотеле, где Мэрион останавливается на ночь, она знакомится с его владельцем - очень странным молодым человеком.', 109, 'США', 0),
  ('Пол Эджкомб — начальник блока смертников в тюрьме «Холодная гора», каждый из узников которого однажды проходит «зеленую милю» по пути к месту казни. Пол повидал много заключённых и надзирателей за время работы. Однако гигант Джон Коффи, обвинённый в страшном преступлении, стал одним из самых необычных обитателей блока.', 189, 'США', 0),
  ('Оставивший родину американец Рик Блэйн, владелец игорного клуба в Касабланке, встречается с покинувшей его несколько лет назад возлюбленной Ильзой, которая приехала в город вместе со своим мужем — борцом антифашистского сопротивления Виктором Лазло. По их следу идут немцы, и Ильза пытается упросить Рика отдать принадлежащие ему важные документы, которые позволят Виктору бежать из Касабланки, чтобы продолжить свою борьбу.', 102, 'США', 1),
  ('Маленький Бродяга встречает красивую слепую девушку, торгующую цветами на улице, которая по ошибке принимает его за богатого герцога. Узнав о том, что операция может вернуть ей зрение, маленький Бродяга пускается на поиски денег.', 87, 'США', 0),
  ('Пострадав в результате несчастного случая, богатый аристократ Филипп нанимает в помощники человека, который менее всего подходит для этой работы, — молодого жителя предместья Дрисса, только что освободившегося из тюрьмы. Несмотря на то, что Филипп прикован к инвалидному креслу, Дриссу удается привнести в размеренную жизнь аристократа дух приключений.', 112, 'Франция', 0),
  ('«Новые времена» — это эмоциональный, выдержанный в комическом ключе, отклик на перекосы машинной эпохи. Маленький Бродяга не желает быть «винтиком» в огромной мега-машине промышленного производства и так похожего на него человеческого общества. Чарли сражается с тупыми богачами, всесильными полицейскими, бездушными начальниками конвейерного производства. Иногда он выигрывает в этих сражениях, но чаще проигрывает, в конце концов, бесстрашно идет навстречу рассвету. Хотя «Новые времена» считается последним немым фильмом Чарли Чаплина, в нем Маленький Бродяга в первый и в последний раз обрел голос. Правда, он не заговорил, а запел в финальных кадрах картины, символизируя прощание с прекрасным периодом немого кинематографа.', 87, 'США', 0),
  ('Известный археолог и специалист по оккультным наукам доктор Джонс получает опасное задание от правительства США. Он должен отправиться на поиски уникальной реликвии - священного Ковчега. Но Индиана и не подозревает, что аналогичный приказ уже получили тайные агенты Адольфа Гитлера...', 115, 'США', 0),
  ('Фильм снят по автобиографии Владислава Шпильмана, одного из лучших пианистов Польши 30-х годов прошлого века. Главный герой фильма — Владек — занимается искусством до тех пор, пока территорию Польши не занимают нацисты. Жизнь всех евреев меняется: их помещают в Варшавское гетто, запрещают работать, унижают, заставляют носить отличительные повязки, а через некоторое время отправляют в концлагерь.', 150, 'Франция, Германия, Великобритания, Польша', 0),
  ('Два лучших выпускника полицейской академии оказались по разные стороны баррикады: один из них — агент мафии в рядах правоохранительных органов, другой — «крот», внедрённый в мафию. Каждый считает своим долгом обнаружить и уничтожить противника, но постоянная жизнь в искажённых реалиях меняет внутренний мир героев.', 151, 'США', 1),
  ('Прикованный из-за сломанной ноги к инвалидному креслу фоторепортер от скуки начинает наблюдать за соседями, живущими в его же доме, чьи окна выходят во внутренний двор. Действие происходит в Гринвич Вилледж. Постоянные наблюдения наталкивают его на подозрение, что один из соседей убил свою жену...', 112, 'США', 0),
  ('Прошло более десяти лет с тех пор, как киборг из 2029 года пытался уничтожить Сару Коннор — женщину, чей будущий сын выиграет войну человечества против машин. Теперь у Сары родился сын Джон и время, когда он поведёт за собой выживших людей на борьбу с машинами, неумолимо приближается. Именно в этот момент из постапокалиптического будущего прибывает новый терминатор — практически неуязвимая модель T-1000, способная принимать любое обличье. Цель нового терминатора уже не Сара, а уничтожение молодого Джона Коннора. Однако шансы Джона на спасение существенно повышаются, когда на помощь приходит перепрограммированный сопротивлением терминатор предыдущего поколения...', 137, 'США, Франция', 0),
  ('Подросток Марти с помощью машины времени, сооружённой его другом-профессором доком Брауном, попадает из 80-х в далекие 50-е. Там он встречается со своими будущими родителями, ещё подростками, и другом-профессором, совсем молодым.', 116, 'США', 0),
  ('Эндрю мечтает стать великим. Казалось бы, вот-вот его мечта осуществится. Юношу замечает настоящий гений, дирижер лучшего в стране оркестра. Желание Эндрю добиться успеха быстро становится одержимостью, а безжалостный наставник продолжает подталкивать его все дальше и дальше – за пределы человеческих возможностей. Кто выйдет победителем из этой схватки?', 106, 'США', 1),
  ('В великой Римской империи не было военачальника, равного генералу Максимусу. Непобедимые легионы, которыми командовал этот благородный воин, боготворили его и могли последовать за ним даже в ад. Но случилось так, что отважный Максимус, готовый сразиться с любым противником в честном бою, оказался бессилен против вероломных придворных интриг. Генерала предали и приговорили к смерти. Чудом избежав гибели, Максимус становится гладиатором. Быстро снискав себе славу в кровавых поединках, он оказывается в знаменитом римском Колизее, на арене которого он встретится в смертельной схватке со своим заклятым врагом...', 155, 'США, Великобритания', 1),
  ('Роберт и Альфред — фокусники-иллюзионисты, которые на рубеже XIX и XX веков соперничали друг с другом в Лондоне. С годами их дружеская конкуренция на профессиональной почве перерастает в настоящую войну. Они готовы на все, чтобы выведать друг у друга секреты фантастических трюков и сорвать их исполнение. Непримиримая вражда, вспыхнувшая между ними, начинает угрожать жизни окружающих их людей…', 130, 'США, Великобритания', 0),
  ('У величественного Короля-Льва Муфасы рождается наследник по имени Симба. Уже в детстве любознательный малыш становится жертвой интриг своего завистливого дяди Шрама, мечтающего о власти. Симба познаёт горе утраты, предательство и изгнание, но в конце концов обретает верных друзей и находит любимую. Закалённый испытаниями, он в нелёгкой борьбе отвоёвывает своё законное место в «Круге жизни», осознав, что значит быть настоящим Королём.', 88, 'США', 0),
  ('Леонард Шелби изысканно и дорого одет, ездит на новеньком «Ягуаре», но проживает в дешевых мотелях. Его цель в жизни — найти убийцу жены. Его проблема — редкая форма амнезии, потеря короткой памяти, помня все до убийства, он не помнит, что было пятнадцать минут назад.', 113, 'США', 0),
  ('Во время Вьетнамской войны капитан Уиллард отправляется вверх по реке в Камбоджу со спецзаданием: найти и убить сумасшедшего полковника Курца, создавшего в отдалённом районе нечто вроде собственного культа. По пути капитан становится свидетелем безумия и ужасов войны.', 179, 'США', 0),
  ('В далеком будущем возвращающийся на Землю грузовой космический корабль перехватывает исходящий с неизвестной планеты неопознанный сигнал. Экипаж, в соответствии с основными инструкциями, обязан найти и исследовать источник сигнала. Оказавшись на планете, астронавты повсюду обнаруживают неопознанные предметы, по виду напоминающие гигантские коконы.', 117, 'США, Великобритания', 0),
  ('Главный герой фильма, еврейский цирюльник, сражается в рядах армии своего государства Томании. Однажды, во время одного из сражений, он спасает жизнь летчику Шульцу, но в результате довольно жесткого приземления ударяется головой, теряет память и долгое время находится в больнице. Шли годы, война давно уже закончилась, и в Томании приходит к власти диктатор Аденоид Хинкель, который яро ненавидит евреев и очень сильно похож на того самого еврейского цирюльника. Хинкель жаждет завоевать весь мир, а в это время цирюльник выходит из больницы и, ни о чем не подозревая, пытается наладить старую жизнь. Однако впереди его ждут невероятные приключения…', 124, 'США', 0),
  ('Молодой сценарист Джо Гиллис сидит на полной «мели», его преследуют кредиторы, ему не на что поесть, ему кажется, что жизнь кончилась. Машина, на которой он отправляется бездумно и бесцельно неизвестно куда, вдруг резко виляет в сторону (лопнула шина!) и влетает во двор особняка знаменитой в прошлом, но увядающей в настоящем кинозвезды Нормы Десмонд…', 110, 'США', 0),
  ('Одержимый мыслью, что коммунисты намереваются украсть у американцев их «бесценные телесные соки», командир военно-воздушной базы генерал Джек Д.Риппер посылает эскадрилью бомбардировщиков с ядерным оружием бомбить СССР. Президент США Маффли пытается спасти положение, он собирает своих советников, включая доблестного генерала Тергидсона и прикованного к инвалидной коляске бывшего нацистского ученого доктора Стрейнджлава.', 95, 'США', 0),
  ('Это рассказ о счастливых днях, когда итальянское кино еще не знало о том, что такое «кризис итальянского кинематографа». В центре разделенной как бы на три части картины — сицилийский мальчишка Сальваторе.', 124, 'Италия, Франция', 0),
  ('Сто тысяч сотрудников, двести тысяч информаторов. Вместе они — щит и меч партии, знаменитое Штази. Их метод — подозрение. Жизнь других — их профессия. Популярный писатель и драматург Георг Драйман так бы и дожил до конца своих дней, обласканный вниманием благодарных читателей и абсолютно неинтересный для спецслужб, если бы не роман с известной театральной актрисой. Слишком известной, чтобы остаться незамеченной влиятельным чиновником из ЦК. Так в жизни Драймана появляются специалисты из Штази.', 137, 'Германия', 0),
  ('Последние дни Второй мировой войны, американская авиация бомбит беззащитные японские города. В водоворот каждодневного кошмара попадают 14-летний Сэйта и его сестренка Сэцуко — потеряв близких, они остались совсем одни. Мальчик в одночасье становится взрослым и осознаёт, что от него зависит жизнь маленькой сестры. Укрывшись в заброшенном убежище, Сэйта и Сэцуко пытаются выжить собственными силами. И пусть в их глазах смертоносный огонь в небе — всего лишь огоньки светлячков, жестокая война не щадит даже невинные детские души, вынося им беспощадный приговор.', 100, 'Япония', 0),
  ('Действие киноромана развертывается в 1916 году на Западном фронте. Полковник Дэкс командует пехотным полком французской армии. Уже два года союзники пытаются переломить ход войны с немцами. Начальник корпуса генерал Брулард приказывает дивизионному генералу Миро атаковать неприступную вражескую позицию, прозванную Муравьиным холмом. Провал операции был очевиден изначально, но когда атака закончилась полной неудачей, генерал приказал судить и расстрелять за трусость троих человек. Полковник Дэкс тщетно пытался защитить солдат, выбранных произвольно. Они отдали свои жизни, чтобы прикрыть глупость и амбиции начальства…', 86, 'США', 0),
  ('Эксцентричный охотник за головами, также известный как Дантист, промышляет отстрелом самых опасных преступников. Работенка пыльная, и без надежного помощника ему не обойтись. Но как найти такого и желательно не очень дорогого? Освобождённый им раб по имени Джанго – прекрасная кандидатура. Правда, у нового помощника свои мотивы – кое с чем надо сперва разобраться.', 165, 'США', 0),
  ('Главный герой - Джек Торренс - приехал в элегантный уединенный отель, чтобы поработать смотрителем во время мертвого сезона вместе со своей женой и сыном. Торренс здесь раньше никогда не бывал. Или это не совсем так? Ответ лежит во мраке, сотканном из преступного кошмара', 146, 'США, Великобритания', 0),
  ('Робот ВАЛЛ·И из года в год прилежно трудится на опустевшей Земле, очищая нашу планету от гор мусора, которые оставили после себя улетевшие в космос люди. Он и не представляет, что совсем скоро произойдут невероятные события, благодаря которым он встретит друзей, поднимется к звездам и даже сумеет изменить к лучшему своих бывших хозяев, совсем позабывших родную Землю.', 98, 'США', 1),
  ('Лестер Бернэм переживает кризис среднего возраста. Его не уважают и не ценят на работе, а от счастливой семьи осталась лишь видимость. У жены Кэролайн страстный роман с коллегой по работе, а угрюмая дочь-подросток Джейн увлечена соседским парнем, побывавшим в психиатрической больнице. Терзаемый душевными муками Лестер впадает в депрессию. Но неожиданно для себя влюбляется в одноклассницу дочери Анджелу, и эта страсть дает Лестеру мощный жизненный импульс. Он ощущает прилив сил и желаний и готов снова радоваться красоте.', 122, 'США', 1),
  ('Восемь лет назад Бэтмен растворился в ночи, превратившись из героя в беглеца. Приняв на себя вину за смерть прокурора Харви Дента, он пожертвовал всем. Вместе с комиссаром Гордоном они решили, что так будет лучше для всех. Пока преступность была раздавлена антикриминальным актом Дента, ложь действовала. Тем не менее, еще опаснее становится появление нового врага Бэйна, чье лицо закрыто маской. Он разворачивает в Готэме чудовищную деятельность, и это вынуждает Брюса Уэйна выйти из импровизированного изгнания. Однако даже надев свой костюм, Бэтмен может проиграть Бэйну. Но с появлением хитрой воровки по прозвищу Женщина — Кошка с загадочным прошлым всё меняется.', 165, 'США', 0),
  ('Юный принц Аситака, убив вепря, навлек на себя смертельное проклятие. Старая знахарка предсказала ему, что только он сам способен изменить свою судьбу. И отважный воин отправился в опасное путешествие. Так он оказался в загадочной стране, где люди под предводительством злой госпожи Эбоси воевали с обитателями леса: духами, демонами и гигантскими существами, каких Аситака раньше никогда не видел. И была с ними принцесса Мононоке - повелительница зверей и дочь волчицы. Теперь судьба всех зависит только от одного воина - принца Аситаки.', 133, 'Япония', 0),
  ('Чужой — совершенный организм. Идеальная машина для убийства, чье физическое превосходство сочетается с его феноменальной жаждой уничтожения. Офицер Элен Рипли и команда космического корабля Ностромо один раз уже встретилась с такой тварью. В живых осталась только Элен. Капсула с Элен найдена спасателями после многих лет блуждания в космосе. Ей сообщают, что планета L.V. 426 колонизирована, и ей придется вернуться туда, где начался ее кошмар, ибо связь с колонистами прервалась. И вот в составе группы космического десанта Рипли отправляется на проклятую планету. Но теперь их там поджидает не один Чужой, а тысячи. Кто сможет выжить в этой войне: чудовища, способные только убивать, или люди, способные мыслить?', 137, 'США', 0),
  ('1988 год. Обыкновенный и ничем непримечательный бизнесмен О Дэ-cу в день трёхлетия своей дочери по пути домой напивается, начинает хулиганить и закономерно попадает в полицейский участок. Из участка его под своё поручительство забирает друг детства. Пока тот звонит жене незадачливого пьяницы, О Дэ-су пропадает. Неизвестные похищают его и помещают в комнату без окон на 15 лет.', 116, 'Республика Корея', 0),
  ('Сэр Уилфрид Робартс — тяжело больной адвокат, которому врачи запретили заниматься уголовными делами. Выполнить их рекомендации он смог только благодаря заботливой домашней медсестре мисс Плимсолл. Но когда его поверенный приводит к нему домой подозреваемого в убийстве Леонарда Воула, Робартс не может устоять и соглашается вести дело.', 116, 'США', 0),
  ('В бурные двадцатые годы, когда Америка веселилась под звуки джаза и каждый бродяга мечтал стать миллионером, в трущобах Нью-Йорка встретились несколько отчаянных парней. Убирая с дороги конкурентов, безжалостно карая предателей и нагло проворачивая хитроумные аферы, они стали королями преступного мира золотой эры «сухого закона». Они поклялись отдать жизнь друг за друга, зная, что самое главное для них - это дружба, долг и справедливость - не утопить в реках крови и бездонном океане денег. Спустя много лет судьба жестоко обошлась с легендарными гангстерами, заставив их исполнить свою клятву. Это случилось однажды в Америке…', 229, 'США, Италия', 0),
  ('Осень 1941 года. Немецкая подводная лодка отправляется в Атлантический океан — место противостояния германского и британского флотов. Жизнь по строгому распорядку, учебные тревоги, воспоминания о семьях и любимых девушках, солёные мужские шуточки. Скоро всё это закончится. Потому что впереди — ад и никакой надежды на спасение.', 150, 'Германия', 0),
  ('В шикарном поместье умирает газетный магнат Чарльз Фостер Кейн, роняя лишь одно слово: «Роузбад». Смерть Кейна вызывает бурную реакцию в обществе, и группа журналистов берётся узнать, кем же он был на самом деле, что он пережил за свою долгую, полную взлётов и падений, жизнь… И что всё-таки значит «Роузбад»?', 119, 'США', 0),
  ('История спортсмена Махавира Сингха Пхогата, который по причине бедности был вынужден бросить спортивную борьбу, но поклялся воспитать сына, который станет в будущем олимпийским чемпионом. Но вот досада — у них с женой родились четыре дочки. Отец сначала отчаялся, но потом, увидев, что подросшие девчонки поколачивают одноклассников, решил всё же сделать из них спортсменок. Тренер из отца получился отменный.', 161, 'Индия', 0),
  ('Скотти Фергюсон - отставной сыщик, которого не назовешь баловнем судьбы: одинокий, оставшийся за бортом своей профессии, да еще и страдающий патологическим страхом высоты. Бывший коллега Гейвин Элстер просит его последить за своей женой Мэделин, одержимой идеей самоубийства...', 128, 'США', 0),
  ('Однажды успешного рекламного агента Роджера Торнхилла ошибочно принимают за агента разведки Кэплана, которого никто не знает в лицо, и хотят его убрать. Обескураженный Роджер пытается бежать. В вагоне-ресторане поезда милая женщина по имени Ив помогает ему скрыться от преследователей...', 136, 'США', 0),
  ('В шестом эпизоде «Звездных войн» Дарт Вейдер создает вторую «Звезду Смерти». Он объединяет все силы зла, чтобы с помощью этого смертоносного оружия нанести последний сокрушительный удар по повстанцам и их союзникам. Люк Скайуокер вместе с принцессой Лейей и верными дроидами R2D2 и C-3PO отправляется спасать своего друга Хана Соло, который попал в плен к отвратительному Джаббе Хатту - могущественному повелителю преступников. Повстанцы высаживаются на планету Эндор, чтобы оттуда вместе с Люком и его отрядом предпринять последнюю атаку на Имперский флот, атаку, от которой зависит судьба Галактики. В этом эпизоде Люк Скайуокер и Дарт Вейдер встречаются в последний раз... ибо из поединка между отцом и сыном живым выйдет только один...', 134, 'США', 0),
  ('Действие фильма начинается в 1280 году в Шотландии. Это история легендарного национального героя Уильяма Уоллеса, посвятившего себя борьбе с англичанами при короле Эдварде Длинноногом. Он рано лишился отца, погибшего от рук англичан, и его забрал к себе дядя Оргайл, который дал ему хорошее образование в Европе. На родину Уильям возвращается уже взрослым человеком, мечтающим завести семью и жить мирной жизнью. Но судьба распорядилась иначе. Его невесту убили англичане, и он начал свой крестовый поход за свободу.', 177, 'США', 1),
  ('Это должно было стать идеальным преступлением. Задумав ограбить ювелирный магазин, криминальный босс Джо Кэбот собрал вместе шестерых опытных и совершенно незнакомых друг с другом преступников. Но с самого начала все пошло не так, и обычный грабеж превратился в кровавую бойню.', 99, 'США', 0),
  ('Маньяк-убийца наводит ужас на жителей Города. Маленькие девочки не успевают домой к обеду, не возвращаются вовсе. Полиция сбилась с ног в поисках преступника, однако все усилия тщетны. Обеспокоенный активностью полицейских, преступный мир города сам объявил погоню за нарушившим табу маньяком.', 118, 'Германия', 0),
  ('Каждый стремится к своей заветной мечте. Сара Голдфарб мечтала сняться в известном телешоу, ее сын Гарольд с другом Тайроном — сказочно разбогатеть, подруга Гарольда Мэрион грезила о собственном модном магазине, но на их пути были всевозможные препятствия. Все они выбирают неочевидные пути достижения своих целей, и мечты по-прежнему остаются недостижимыми, а жизни героев рушатся безвозвратно.', 102, 'США', 0),
  ('Знаете ли вы, что все события, происходящие в нашем мире, даже самые незначительные, взаимосвязаны самым удивительным и чудесным образом? Как полет крошечной мухи может вызвать где-то далеко мощный ураган, так и странные и, на первый взгляд, непонятные поступки тихой и одинокой девушки, живущей в мире своих фантазий, могут навсегда изменить жизнь совершенно разных людей, подарив им счастье и раскрасив окружающий мир яркими, головокружительными красками. Эту девушку зовут Амели Пулен.', 122, 'Франция, Германия', 0),
  ('Фильм расскажет историю чудесного спасения более трехсот тысяч солдат в ходе Дюнкеркской операции, имевшей место в начале Второй мировой войны. События начинаются с окружения сотен тысяч британских и союзных войск силами противника. Пойманные в ловушку на пляже, и находясь спиной к морю, они сталкиваются лицом к лицу с неразрешимой ситуацией, тем временем как тиски противника сжимаются все сильнее и сильнее.', 106, 'Великобритания, США, Франция, Нидерланды', 0),
  ('Маленький мальчик 8 лет Ишан Авасти с рождения немного отличается от других детей. Ему дается с трудом то, что у других получается очень легко. Мир не понимает этого ребенка, как и его собственные родители. Когда Ишан в третий раз проваливает экзамены в школе, его отец в наказание отправляет мальчика в школу-интернат. Одиночество губит ребенка, он осуждает себя за расставание с родителями, но и их не может простить. Однажды в жизни Ишана появляется временный учитель рисования Рам Никум — единственный, кто понимает этого ребенка. Рам ставит перед собой цель изменить жизнь мальчика и отношение к нему.', 165, 'Индия', 0),
  ('В фильме был произведен исчерпывающий анализ причин преступности среди молодежи, нетерпимости нового поколения к привычным моральным ценностям и жизненным устоям современного общества. Безжалостный лидер банды подростков, совершающей убийства и изнасилования, попадает в тюрьму и подвергается специальной обработке по подавлению подсознательного стремления к насилию. Но жизнь за воротами тюрьмы такова, что меры, принятые по «исправлению жестокости характера» не могут ничего изменить.', 136, 'Великобритания, США', 0),
  ('История о парне из Токио и девушке из провинции, которые обнаруживают, что между ними существует странная и необъяснимая связь. Во сне они меняются телами и проживают жизни друг друга. Но однажды эта способность исчезает так же внезапно, как появилась. Таки решает во что бы то ни стало отыскать Мицуху.', 107, 'Япония', 0),
  ('Фильм рассказывает о легендарном герое Первой мировой войны - английском разведчике Т.Э. Лоуренсе, действовавшем среди арабских племен и возглавившем их в походе против турок.', 222, 'Великобритания, США', 1),
  ('Сластолюбивый страховой агент Уолтер Нефф отправляется продлить полис на машину некоему мистеру Дитрихсону. В его доме он встречается с миссис Дитрихсон и мгновенно воспламеняется. Далее, как и во всех «черных» фильмах, красотка делает Уолтеру предложение, от которого он не может отказаться и… совершает первый шаг в объятия Смерти.', 107, 'США', 0),
  ('Тусклый свет слепых фонарей, скелеты фабричных труб, задыхающихся в собственном дыму. Вавилонские башни небоскребов, все это - ад Нового времени, Нью-Йорк. Ветеран вьетнамской войны Трэйвис Бикл ведет свое одинокое такси по ночным улицам бесконечного города, и перед ним разворачивается мрачная панорама человеческих грехов. Как ветхозаветный пророк, он надеется, что однажды небеса пошлют на землю спасительный дождь, который очистит Нью-Йорк от вековой грязи. А когда умирает надежда, остается только ненависть. Огненный ливень обрушится на головы грешников. Таксист позаботится об этом.', 113, 'США', 0),
  ('1781 год. Антонио Сальери успешно справляется с обязанностями придворного композитора при императоре Иосифе II. Когда же при дворе появляется Моцарт, Сальери к своему ужасу обнаруживает, что божественный музыкальный дар, которым он так желает обладать, был отпущен какому-то непристойному, проказливому шуту. Ослепленный завистью, он замышляет во что бы то ни стало уничтожить Моцарта...', 153, 'США', 1),
  ('Застенчивый и меланхоличный Джоэл живёт ничем не примечательной серой и унылой жизнью. Но однажды вместо привычного рабочего маршрута молодой человек вдруг садится на электричку в другом направлении и устремляется к морю. На песчаном берегу Джоэл замечает девушку с ярко-синими волосами. На обратном пути они знакомятся в вагоне электрички и парень понимает, что у них с Клементиной очень много общего, как будто он уже знает эту девушку. Совсем скоро Джоэл поймёт, что действительно был знаком с ней, более того - они были парой.', 108, 'США', 0),
  ('Фильм о юристе из Алабамы, воспитывающем без матери своих двоих детей. Помимо темы расовых предрассудков на Юге, это рассказ о том, как двое детей из мира фантазий делают первые шаги в совершенно иной мир подростков, где уже формируются такие понятия, как благородство, сострадание, справедливость и неравенство. В обстановке экономической депрессии, преисполненной фанатизма и ненависти, мудрый, мягкий, с тихой речью и великолепными манерами алабамский юрист должен защищать в суде афроамериканца, ложно обвиненного в изнасиловании.', 129, 'США', 0),
  ('Американская база подготовки новобранцев корпуса морской пехоты. Жесточайшая, бесчеловечная система призвана превратить домашних мальчишек в натренированных хладнокровных убийц. Их готовят к войне во Вьетнаме, где им придется часто задавать себе вопрос «Разве война - это не ад?», а иначе просто невозможно убивать каждый день, не понимая, для чего это?', 116, 'США, Великобритания', 0),
  ('Экипаж космического корабля «Дискавери» — капитаны Дэйв Боумэн, Фрэнк Пул и их бортовой компьютер HAL 9000 — должны исследовать район галактики и понять, почему инопланетяне следят за Землей. На этом пути их ждет множество неожиданных открытий.', 149, 'США, Великобритания', 0),
  ('Основа этого легендарного фильма — дюжина старых песен, написанных для различных бродвейских шоу. Дон Локвуд — популярная звезда немого кино вспоминает о том, как он пришел к славе… но вдруг счастье покидает его — наступила эра звукового кино. Нужно осваивать новые горизонты.', 103, 'США', 0),
  ('Энди почти 18 лет, ему остаётся 3 дня до отправки в колледж, в то время как его игрушки, в том числе Вуди и Базз Лайтер, гадают о своём будущем. Куда забросит их судьба? На чердак, на свалку или, может быть, в детский сад «Солнышко»? События развиваются в самом неожиданном направлении, и приключения полюбившихся героев продолжаются!', 103, 'США', 1),
  ('В 1930-е годы двое отпетых мошенников пытаются отомстить главарю мафии за смерть их общего друга с помощью одной из самых неожиданных афер в истории кино, которая к тому же заканчивается удивительным и забавным образом.', 130, 'США', 1),
  ('Каждый ребенок верит, что когда он оставляет свои игрушки одни, они начинают заниматься своими делами. Этот мультипликационный фильм позволяет убедиться им в своей правоте. Основная идея фильма — каждая игрушка боится надоесть, стать забытой, замененной другой, так как весь смысл их существования — доставлять счастье своим хозяевам.', 81, 'США', 0),
  ('Компания друзей отправляется в путешествие в поисках своего сокурсника, бесследно пропавшего прямо в день выпускного.', 170, 'Индия', 0),
  ('Послевоенный Рим, давно неработающий Антонио вне себя от радости — он наконец-то находит работу. Но в первый же трудовой день у него украли велосипед, без которого невозможно работать! Вместе с маленьким сыном Бруно он пытается найти в огромном городе украденный велосипед, от которого зависит жизнь его семьи…', 93, 'Италия', 0),
  ('Вторая мировая война, в оккупированной немцами Франции группа американских солдат-евреев наводит страх на нацистов, жестоко убивая и скальпируя солдат.', 153, 'Германия, США', 0),
  ('Фильм «Малыш» стал первым полнометражным фильмом Чаплина-режиссера. Актера на главную роль Малыша — пятилетнего Джекки Кугана — Чаплин нашел на сцене, где ребенок выступал на равных со взрослыми. Джекки оказался прирожденным актером и схватывал все на лету. Фильм «Малыш» — это история ребенка, брошенного матерью вскоре после рождения. После цепи приключений подкидыша находит житель трущоб бедный Чарли, который учит ребенка тому, что знает сам.', 68, 'США', 0),
  ('Четырехпалый Френки должен был переправить краденый алмаз из Англии в США своему боссу Эви. Но вместо этого герой попадает в эпицентр больших неприятностей. Сделав ставку на подпольном боксерском поединке, Френки попадает в круговорот весьма нежелательных событий. Вокруг героя и его груза разворачивается сложная интрига с участием множества колоритных персонажей лондонского дна - русского гангстера, троих незадачливых грабителей, хитрого боксера и угрюмого громилы грозного мафиози. Каждый норовит в одиночку сорвать Большой Куш.', 104, 'Великобритания, США', 0);
 INSERT INTO actors (id, full_name, description, birthday_at) values
 	(1, 'Тим Роббинс', 'Американский актёр кино и телевидения, кинорежиссёр, сценарист и кинопродюсер, получивший известность благодаря ролям в фильмах «Дархэмский бык» (1988), «Лестница Иакова» (1990), «Игрок» (1992), «Подручный Хадсакера» (1994), «Побег из Шоушенка» (1994), «Таинственная река» (2003), принёсшая ему премию «Оскар» за лучшую мужскую роль второго плана, и «Война миров» (2005). За постановку криминальной драмы «Мертвец идёт» выдвигался на «Оскар» за лучшую режиссёрскую работу в 1996 году.', '1958-10-16'),
 	(2, 'Морган Фримен', 'Американский актёр кино и озвучивания, режиссёр. Лауреат премии «Оскар», премии «Серебряный медведь» и двух «Золотых глобусов». Также Фримен известен своим характерным спокойным глубоким голосом и мастерством рассказчика. В числе известных актёрских работ Фримена такие фильмы, как «Шофёр мисс Дэйзи», «Побег из Шоушенка», «Семь», «Счастливое число Слевина», «Малышка на миллион» и «Пока не сыграл в ящик».', '1937-06-01'),
 	(3, 'Клэнси Браун', 'Американский актёр. Известен благодаря фильмам «Горец», «Невеста», «Побег из Шоушенка», «Приключения Бакару Банзая: Через восьмое измерение», «Любовь, ложь и убийство», «Флаббер», мультсериалу «Губка Боб Квадратные Штаны», в котором озвучил Мистера Крабса. Он также известен ролью Хэнка Андерсона в видеоигре «Detroit: Become Human» и озвучиванием Доктора Нео Кортекса в серии игр «Crash Bandicoot».', '1959-01-05'),
 	(4, 'Марлон Брандо', 'Американский актёр кино и телевидения, кинорежиссёр и политический активист. По мнению С. Канфера, является одним из величайших актёров в истории. Говоря о вкладе Брандо в мировое киноискусство, один из его биографов, Стефан Канфер, однажды произнёс: «Существовал кинематограф до Брандо и после Брандо, также как живопись до Пикассо и после Пикассо, литература до Хемингуэя и после Хемингуэя и поп-музыка до Синатры и после Синатры …». Схожего мнения придерживалась историк кино Молли Хаскелл: «Легенду киноэкрана можно охарактеризовать одним словом. Брандо. Как Гарбо. Или Фидо. … Есть только один Брандо».', '1924-04-03'),
 	(5, 'Аль Пачино', 'Американский актёр, режиссёр и сценарист. Наиболее знаменит своими ролями гангстеров — Майкл Корлеоне в трилогии «Крёстный отец» Фрэнсиса Форда Копполы и Тони Монтана в фильме Брайана Де Пальма «Лицо со шрамом». Роль Фрэнка Слейда в фильме «Запах женщины» принесла актёру «Оскар» в номинации Лучшая мужская роль, что стало его первым успехом после семи предыдущих номинаций.', '1940-04-25'),
 	(6, 'Джеймс Каан', 'Американский актёр, мастер 6 дана каратэ Госоку Рю, обладатель титула Сокэ Дай International Karate Association. Наиболее известен по участию в фильмах «Путешествие в Шайло», «Крёстный отец», «Роллербол», «Мост слишком далеко», «Мизери», «Стиратель» и «Криминальная фишка от Генри». Номинант на премию «Оскар» (1972), номинант на премию «Эмми» (1971), трёхкратный номинант на премию «Золотой глобус» (1972, 1974, 1975), лауреат премии «Сатурн» (1975).', '1940-03-26'),
 	(7, 'Роберт Де Ниро', 'Американский актёр, режиссёр и продюсер. Обладатель премий «Золотой глобус» (1981, 2011) и «Оскар» (1975, 1981)', '1943-08-17'),
 	(8, 'Роберт Дюваль', 'Американский актёр, режиссёр и продюсер, снявшийся в таких значительных картинах, как «Военно-полевой госпиталь», «Крёстный отец» и «Апокалипсис сегодня». Обладатель «Оскара» за лучшую мужскую роль в фильме 1983 года «Нежное милосердие» (а также шестикратный номинант на эту награду[6]), лауреат премии BAFTA, двух «Эмми» и четырёх «Золотых глобусов».', '1931-01-05'),
 	(9, 'Кристиан Бейл', 'Британо-американский актёр. Лауреат премий «Золотой глобус» и «Оскар» в номинации «Лучшая мужская роль второго плана» за фильм «Боец» (2010). Был номинирован на «Оскар», BAFTA и «Золотой глобус» за роль мошенника Ирвинга Розенфельда в криминальной трагикомедии «Афера по-американски» (2014), за лучшую мужскую роль, а также за роль в фильме «Игра на понижение» (2016), за лучшую мужскую роль второго плана. Заработал номинацию на Оскар и «Золотой глобус» за роль Дика Чейни в фильме «Власть». Также широко известен как исполнитель роли Бэтмена в трилогии Кристофера Нолана — «Бэтмен: Начало» (2005), «Тёмный рыцарь» (2008) и «Тёмный рыцарь: Возрождение легенды» (2012).', '1974-01-30'),
 	(10, 'Хит Леджер', 'Австралийский актёр и клипмейкер. После успешных ролей в кино и на телевидении Австралии в 1990-х годах Леджер в 1998 году переехал в США с целью развития актёрской карьеры. В Голливуде он снялся в 19 фильмах, включая «10 причин моей ненависти» (1999), «Патриот» (2000), «История рыцаря» (2001), «Горбатая гора» (2005) и «Тёмный рыцарь» (2008). В дополнение к актёрской карьере он продюсировал, снимал музыкальные клипы и стремился стать режиссёром.', '1979-04-04'),
 	(11, 'Гэри Олдмен', 'Английский актёр, режиссёр, продюсер и музыкант. Олдмен прославился после исполнения главных ролей в кинокартинах «Сид и Нэнси» и «Навострите ваши уши», после выхода которых кинокритик Роджер Эберт назвал его «лучшим молодым британским актёром современности»', '1959-03-21'),
 	(12, 'Лиам Нисон', 'Британский актёр театра, кино и телевидения ирландского происхождения. В числе его известных актёрских работ такие фильмы, как «Список Шиндлера», «Роб Рой», «Отверженные», «Реальная любовь», «Неизвестный», «Воздушный маршал», «Молчание» и кинотрилогия «Заложница» и появление в двух из трёх фильмов трилогии Кристофера Нолана о Бэтмене, «Бэтмен: Начало» и «Тёмный рыцарь: Возрождение легенды».', '1952-06-07'),
	(13, 'Бен Кингсли', 'Британский актёр театра, кино, телевидения и озвучивания индийского происхождения. Лауреат премии «Оскар» (1983), двух премий BAFTA (1983 — дважды), двух премий «Золотой глобус» (1983 — дважды), «Грэмми» (1985), «Премии Гильдии киноактёров США» (2002) и премии «Сатурн» (2014).', '1943-12-31'),
	(14, 'Рэйф Файнс', 'Британский актёр театра и кино, кинорежиссёр и продюсер, посол доброй воли ЮНИСЕФ. Обладатель кинематографической премии BAFTA и театральной премии «Тони», двукратный номинант на премию «Оскар», пятикратный номинант на премию «Золотой глобус». Прославился благодаря роли гауптштурмфюрера СС Амона Гёта в фильме Стивена Спилберга «Список Шиндлера» и по роли лорда Волан-де-Морта в серии фильмов о Гарри Поттере, а также по фильмам «Английский пациент», «Красный дракон», «Залечь на дно в Брюгге», «Чтец», «007: Координаты «Скайфолл»», «007: Спектр» и «Отель «Гранд Будапешт»» и по роли Хитклифа в экранизации романа «Грозовой перевал».', '1962-12-22'),
	(15, 'Джон Траволта', 'Американский актёр, танцор и певец, лауреат премий «Золотой глобус» и «Эмми». Траволта впервые прославился в 1970-х после участия в кассовых картинах «Лихорадка субботнего вечера» и «Бриолин». В 1980-х его актёрская карьера испытала спад, но дела снова пошли в гору в 1990-х: он появился в фильме «Криминальное чтиво», за которым последовали «Без лица», «Пароль „Рыба-меч“», «Реальные кабаны», «Лак для волос» и «Из Парижа с любовью».', '1954-02-18'),
	(16, 'Сэмюэл Л. Джексон', 'Американский актёр кино и телевидения, продюсер. В 1991 году получил признание критиков за свою роль в фильме «Тропическая лихорадка», а после появился в таких фильмах, как «Славные парни», «Игры патриотов» и «Парк Юрского периода». В 1994 году снялся в фильме «Криминальное чтиво», что вывело его на уровень суперзвёзд и принесло несколько наград. С тех пор Джексон снялся более чем в 100 фильмах и завоевал множество наград на протяжении всей своей карьеры', '1948-12-21'),
	(17, 'Брюс Уиллис', 'Американский актёр, продюсер и музыкант. Один из самых высокооплачиваемых актёров Голливуда. Наибольшую известность получил благодаря роли полицейского Джона Макклейна в серии фильмов «Крепкий орешек», а также таким картинам, как «Смерть ей к лицу» (1992), «Криминальное чтиво» (1994), «12 обезьян» (1995), «Пятый элемент» (1997), «Армагеддон» (1998), «Шестое чувство» (1999), «Неуязвимый» (2000), «Город грехов» (2005), «Счастливое число Слевина» (2005), «РЭД» (2010).', '1955-03-19'),
	(18, 'Ума Турман', 'Американская актриса кино и телевидения, бывшая модель. Сыграла главные роли в различных кинолентах, от романтических комедий и драм до научно-фантастических фильмов и боевиков. После ранних ролей в таких фильмах, как «Опасные связи», она добилась международной известности в 1994 году, сыграв роль в «Криминальном чтиве» Квентина Тарантино, за которую была номинирована на премии «Оскар», «Золотой глобус» и BAFTA.', '1970-04-29'),
	(19, 'Элайджа Вуд', 'Американский актёр. Наиболее известен по роли Фродо Бэггинса в трилогии «Властелин колец» (2001—2003).', '1981-01-28'),
	(20, 'Иэн Маккеллен', 'Британский актёр. Лауреат премий «Золотой глобус» и «Тони», а также шести премий Лоренса Оливье. Номинант на премии «Оскар» и «Эмми». Маккеллен получил широкое признание как мастер шекспировского репертуара, после чего снискал мировую славу благодаря участию в крупнобюджетных международных кинопроектах (Магнето в серии фильмов «Люди Икс», Гэндальф в кинотрилогиях «Властелин колец» и «Хоббит»).', '1939-05-25'),
	(21, 'Орландо Блум', 'Английский киноактёр. Получил известность благодаря ролям в кинотрилогии «Властелин колец» (2001, 2002, 2003) и её приквеле «Хоббит», приключенческом фильме «Пираты Карибского моря: Проклятие Чёрной жемчужины» (2003) и его продолжениях «Пираты Карибского моря: Сундук мертвеца» (2006), «Пираты Карибского моря: На краю света» (2007) и «Пираты Карибского моря: Мертвецы не рассказывают сказки» (2017).', '1977-01-13'),
	(22, 'Вигго Мортенсен', 'Датско-американский актёр, писатель, фотограф и художник. Трёхкратный номинант на премии «Оскар» и BAFTA, а также четырёхкратный номинант на премию «Золотой глобус». Мортенсен дебютировал в кино с ролью в триллере «Свидетель» (1985), после чего появился в таких фильмах, как «Бегущий индеец» (1991), «Путь Карлито» (1993), «Багровый прилив» (1995), «Дневной свет» (1996), «Портрет леди» (1996), «Солдат Джейн» (1997), «Идеальное убийство» (1998), «Прогулка по Луне» (1999) и «28 дней» (2000).', '1958-10-20'),
	(23, 'Клинт Иствуд', 'Американский киноактёр, кинорежиссёр, композитор. Обладатель пяти наград Американской киноакадемии: четырёх премий «Оскар» в номинациях «Лучший режиссёр» и «Лучший фильм года», а также «Награды имени Ирвинга Тальберга» за продюсерский вклад в киноискусство и Национальной медали США в области искусств (2009). Лауреат премии Почётный Сезар.', '1930-05-31'),
	(24, 'Илай Уоллак', 'Американский актёр. Наиболее известные сыгранные им роли — Кальвера в «Великолепной семёрке» Джона Стёрджеса, Туко в фильме Серджо Леоне «Хороший, плохой, злой», дон Альтабелло в фильме Фрэнсиса Форда Копполы «Крёстный отец 3». Лауреат премий BAFTA, «Тони» и «Эмми», обладатель почётной премии «Оскар» за достижения на протяжении всей кинокарьеры (2011).', '1915-12-07'),
	(25, 'Эдвард Нортон', 'Американский актёр, режиссёр, продюсер, сценарист и активист. Лауреат премии «Золотой глобус» и трёхкратный номинант на премию «Оскар».', '1969-08-18'),
	(26, 'Брэд Питт', 'Американский актёр кино, телевидения и озвучивания, кинопродюсер. Лауреат премии «Золотой глобус» за 1995 год — за роль второго плана в ленте «12 обезьян». Обладатель премии «Оскар» как один из продюсеров фильма «12 лет рабства» — победителя в категории «Лучший фильм» на церемонии 2014 года и за лучшую мужскую роль второго плана в картине «Однажды в… Голливуде» (2020)[1]. До этого четыре раза номинировался на премию «Оскар» (трижды — как актёр и один раз — как продюсер).', '1963-12-18'),
	(27, 'Хелена Бонэм Картер', 'Британская актриса театра, кино, телевидения и озвучивания. Лауреат премии BAFTA (2011), восьмикратная номинантка на премию «Золотой глобус» (1994, 1998, 1999, 2003, 2008, 2011, 2014, 2020), двукратная номинантка на премию «Оскар» (1998, 2011). Стала широко известна после исполнения ведущих ролей в экранизациях произведений Э. М. Форстера («Комната с видом», «Там, где даже ангелы боятся появиться», «Говардс Энд»).', '1966-05-26'),
	(28, 'Шон Бин', 'Британский актёр театра и кино. Известен благодаря ролям Боромира в кинотрилогии «Властелин колец», Эддарда Старка в телесериале HBO «Игра престолов» и Ричарда Шарпа в телесериале «Приключения королевского стрелка Шарпа». Другие заметные работы включают роль Алека Тревельяна, агента 006 — противника Джеймса Бонда в фильме «Золотой глаз», Одиссея в «Трое», Яна Хау в фильме «Сокровище нации», Вронского в «Анне Карениной» (1997), Кристофера ДаСилвы в фильме «Сайлент Хилл», клерика Эррола Партриджа в фильме «Эквилибриум», также озвучивал компьютерные игры, в том числе Мартина Септима в «The Elder Scrolls IV: Oblivion» и рассказчика в Sid Meier`s Civilization VI.', '1959-04-17'),
	(29, 'Том Хэнкс', 'Американский актёр, продюсер, режиссёр, сценарист и писатель. Среди наиболее известных фильмов с участием Хэнкса — «Всплеск» (1984), «Большой» (1988), «Тёрнер и Хуч» (1989), «Их собственная лига» (1992), «Неспящие в Сиэтле» (1993), «Аполлон-13» (1995), «Вам письмо» (1998), «Зелёная миля» (1999), «Изгой» (2000), «Проклятый путь» (2002), «Облачный атлас» (2012), «Капитан Филлипс» (2013), «Спасти мистера Бэнкса» (2013), «Чудо на Гудзоне» (2016) и «Прекрасный день по соседству» (2019). Хэнкс также известен по роли Роберта Лэнгдона в экранизациях книг Дэна Брауна, и озвучиванию Шерифа Вуди в серии мультфильмов «История игрушек».', '1956-07-09'),
	(30, 'Робин Райт', 'Американская актриса кино и телевидения. Обладательница премии «Золотой глобус» и семикратная номинантка на премию «Эмми».', '1966-04-08'),
	(31, 'Марк Хэмилл', 'Американский актёр и сценарист. Наиболее известен по роли Люка Скайуокера в киносаге «Звёздные войны».', '1951-09-2'),
	(32, 'Харрисон Форд', 'Американский актёр и продюсер. Номинант на премию «Оскар», BAFTA, четырёхкратный номинант и обладатель специальной премии «Золотой глобус» а также почётного Сезара. Наиболее известен по участию в серии фильмов «Звёздные войны» ролью Хана Соло и серии фильмов об Индиане Джонсе, дилогии «Бегущий по лезвию», а также по фильмам «Игры патриотов», «Беглец», «Самолёт президента», «К-19» и «Свидетель».', '1942-07-13'),
	(33, 'Кэрри Фишер', 'Американская актриса, сценаристка и прозаик, дочь актрисы Дебби Рейнольдс и певца Эдди Фишера. Известность получила благодаря исполнению роли принцессы Леи Органы в киносаге «Звёздные войны».', '1956-10-21'),
	(34, 'Билли Ди Уильямс', 'Американский актёр, художник, певец и писатель. Известен исполнением роли Лэндо Калриссиана в киносаге «Звёздные войны».', '1937-04-06'),
	(35, 'Леонардо Ди Каприо', 'Американский актёр и продюсер. Четыре раза был номинирован на премию «Оскар» за лучшую мужскую роль в фильмах «Авиатор» (2004), «Кровавый алмаз» (2006), «Волк с Уолл-стрит» (2013) и «Выживший» (2016), а также за лучшую мужскую роль второго плана в фильме «Что гложет Гилберта Грэйпа» (1994). Четыре раза был номинирован на премию BAFTA и одиннадцать раз на премию «Золотой глобус». В 2016 году стал обладателем премии «Оскар» в номинации «Лучшая мужская роль», а также награды за лучшую мужскую роль британской премии BAFTA за исполнение роли Хью Гласса в фильме «Выживший». Лауреат трёх премий «Золотой глобус» за лучшую мужскую роль в драме за роль Говарда Хьюза в фильме «Авиатор» (2004), обладатель премии в номинации «Лучшая мужская роль в комедии или мюзикле» за роль Джордана Белфорта в фильме «Волк с Уолл-стрит» (2013) и в номинации «Лучшая мужская роль в драме» за роль в фильме «Выживший» (2016). Номинант премий «Сатурн», Гильдии киноактёров США и «Спутник».', '1974-11-11'),
	(36, 'Кэн Ватанабэ', 'Японский актёр театра, кино и телевидения. За рубежом наиболее известен ролями предводителя повстанцев даймё Кацумото в фильме «Последний самурай», генерала Курибаяси в фильме «Письма с Иводзимы» и предпринимателя Сайто в фильме «Начало». Многократный лауреат «Japanese Academy Awards», номинант на премии «Оскар» и «Золотой глобус» в категории «лучшая мужская роль второго плана» в 2004 году, за роль в фильме «Последний самурай».', '1959-10-21'),
	(37, 'Джозеф Гордон-Левитт', 'Американский актёр и кинорежиссёр, двукратный номинант на премию «Золотой глобус» (2010, 2012). Наиболее заметные фильмы с его участием: «Начало», «Тёмный рыцарь: Возрождение легенды», «Бросок кобры», «Петля времени», «Линкольн», «Сноуден», «Жизнь прекрасна», «Обман», «500 дней лета», «Третья планета от Солнца», «10 причин моей ненависти», «Прогулка», «Кирпич», «Город грехов 2: Женщина, ради которой стоит убивать».', '1981-02-17'),
	(38, 'Джек Николсон', 'Американский актёр, кинорежиссёр, сценарист и продюсер. Николсон считается одним из величайших актёров в истории кино. Он был номинирован на премию «Оскар» рекордные 12 раз, став победителем трижды. Он также является одним из двух актёров (наряду с сэром Майклом Кейном), выдвигавшихся на «Оскар» за лучшую роль (главную или второго плана) в четырёх разных десятилетиях: 1970-х, 1980-х, 1990-х и 2000-х. В 1994 году он стал одним из самых молодых актёров, награждённых премией Американского института киноискусства за жизненные достижения[3]. Джек Николсон также является обладателем множества других наград, в том числе BAFTA, семи «Золотых глобусов», шести премий Национального совета кинокритиков США и лауреатом премии Гильдии киноактёров США.', '1937-04-22'),
	(39, 'Луиза Флетчер', 'Американская актриса, известная благодаря роли властной сестры Рэтчед в культовом фильме «Пролетая над гнездом кукушки» (1975), за которую она получила премию «Оскар» и «Золотой глобус».', '1934-07-22'),
	(40, 'Рэй Лиотта', 'Американский актёр, лауреат премии «Эмми». Наиболее известен по участию в фильмах «Славные парни», «Кокаин», «Ганнибал», «Идентификация», «Револьвер», «Козырные тузы» и «Реальные кабаны».', '1954-12-18'),
	(41, 'Джо Пеши', 'Американский актёр, комик и певец. Наибольшую известность получил благодаря многолетнему сотрудничеству с Мартином Скорсезе в фильмах «Бешеный бык» (1980), «Славные парни» (1990), «Казино» (1995) и «Ирландец» (2019). Роль Томми Девито в «Славных парнях» принесла Пеши премию «Оскар» в категории «Лучший актёр второго плана»; за роль в «Бешеном быке» был отмечен премией BAFTA как лучший новичок.', '1943-02-09'),
	(42, 'Киану Ривз', 'Канадский актёр, кинорежиссёр, кинопродюсер и музыкант (бас-гитарист). Наиболее известен своими ролями в киносериях «Матрица» (1999—2003), «Билл и Тед» (англ.)русск. (1989—2020) и «Джон Уик» (2014—2019), а также в фильмах «На гребне волны» (1991), «Мой личный штат Айдахо» (1991), «Дракула» (1992), «Скорость» (1994), «Джонни-мнемоник» (1995), «Адвокат дьявола» (1997), «Константин: Повелитель тьмы» (2005) и «Короли улиц» (2008).', '1964-09-02'),
	(43, 'Лоуренс Фишборн', 'Американский актёр и продюсер. Лауреат премий «Эмми» и «Тони», а также номинант на премии «Оскар» и «Золотой глобус». Наиболее известен как исполнитель роли Морфеуса в трилогии «Матрица», а также по ролям в фильмах «Апокалипсис сегодня» (1979) и «Ребята по соседству» (1991), и сериале «Ганнибал» (2013—2015).', '1961-07-30'),
	(44, 'Керри-Энн Мосс', 'Канадская актриса, получившая мировую известность после исполнения роли Тринити в трилогии «Матрица». Также известна по исполнению роли Джери Хогарт в телесериалах Кинематографической вселенной Marvel.', '1967-08-21'),
	(45, 'Такаси Симура', 'Японский киноактёр. Снялся более чем в 430 фильмах. Наиболее известен по фильмам Акиры Куросавы, а также первым двум фильмам серии Годзилла.', '1905-03-12'),
	(46, 'Тосиро Мифунэ', 'Японский актёр. Наиболее известен по ролям в фильмах режиссёра Акиры Куросавы.', '1920-04-01'),
	(47, 'Питер Мейхью', 'Английский и американский актёр, филантроп и предприниматель. Наиболее известен по роли Чубакки в IV—VII фильмах саги «Звёздные войны».', '1944-05-19'),
	(48, 'Энтони Хопкинс', 'Британский и американский актёр театра и кино, кинорежиссёр, композитор. Наиболее известен по кинематографическому образу серийного убийцы-каннибала доктора Ганнибала Лектера, воплощённому в фильмах «Молчание ягнят», «Ганнибал» и «Красный дракон». В числе известных актёрских работ Хопкинса такие фильмы, как «Лев зимой», «Магия», «Человек-слон», «Дракула Брэма Стокера», «Остаток дня», «Страна теней», «Легенды осени», «Никсон», «Амистад», «Маска Зорро», «Знакомьтесь, Джо Блэк», «Ной», «Самый быстрый „Индиан“» и другие.', '1937-12-31'),
	(49, 'Джоди Фостер', 'Американская актриса, режиссёр и продюсер. Лауреат двух премий «Оскар» (1989, 1992), трёх премий BAFTA (1977 — дважды, 1992), трёх «Золотых глобусов» (1989, 1992, 2013), двух премий «Сатурн» (1978, 1998), а также премии «Независимый дух» (1989) и премии Гильдии киноактёров США (1995). Обладательница именной звезды на голливудской «Аллее славы».', '1962-12-19'),
	(50, 'Джеймс Стюарт', 'Американский киноактёр, лауреат премии «Оскар» (1941) за лучшую мужскую роль в картине «Филадельфийская история». Боевой лётчик, ветеран Второй мировой войны и войны во Вьетнаме, бригадный генерал.', '1908-05-20'),
	(51, 'Донна Рид', 'Американская актриса, обладательница премии «Оскар» и «Золотой глобус».', '1921-01-27'),
	(52, 'Роберто Бениньи', 'Итальянский актёр, режиссёр, сценарист и продюсер, лауреат премий «Оскар» и BAFTA.', '1952-10-27'),
	(53, 'Николетта Браски', 'Итальянская актриса и продюсер, также известна по своим актёрским работам совместно с мужем, актёром и режиссёром Роберто Бениньи.', '1960-04-19'),
	(54, 'Гэбриэл Бирн', 'Ирландский актёр театра, кино и телевидения. Лауреат премии «Золотой глобус».', '1950-05-12'),
	(55, 'Стивен Болдуин', 'Американский актёр. Младший из четырёх братьев-актёров Болдуинов (другие трое — Алек, Дэниел и Уильям).', '1966-05-12'),
	(56, 'Кевин Спейсин', 'Американский актёр, кинорежиссёр, сценарист, продюсер и крунер. Лауреат многочисленных наград, в том числе двух премий «Оскар» (1996, 2000), BAFTA (2000), «Золотой глобус» (2015), «Тони» (1991), номинант на премии «Эмми» (2008, 2013, 2014) и «Грэмми» (2006). Почётный командор (2010) и рыцарь-командор (2015) ордена Британской империи.', '1959-07-26'),
	(57, 'Жан Рено', 'Французский актёр испанского происхождения. В кино снимается с 1979 года.', '1948-07-30'),
	(58, 'Натали Портман', 'Американская актриса театра и кино израильского происхождения, кинорежиссёр, сценаристка и продюсер. Наибольшую известность получила благодаря участию в фильмах «Леон» (1994, дебютная роль) и «Близость» (2004, премия «Золотой глобус» и номинация на премии BAFTA и «Оскар» за лучшую женскую роль второго плана), а также благодаря трилогии-приквелу к «Звёздным войнам». Обладательница премий «Оскар», «Золотой глобус», BAFTA и «Сатурн» в номинации «Лучшая женская роль» в фильме «Чёрный лебедь» (2010).', '1981-06-09'),
	(59, 'Мэтт Деймон', 'Американский актёр кино, телевидения и озвучивания, сценарист и продюсер. Лауреат премии «Оскар» и двух премий «Золотой глобус», а также номинант на премию «BAFTA».', '1970-10-08'),
	(60, 'Эдвард Фёрлонг', 'Американский актёр и певец, наиболее известный по ролям Джона Коннора в фильме «Терминатор 2: Судный день» и Дэнни Виньярда в драме «Американская история Икс».', '1977-08-02'),
	(61, 'Чарлз Бронсон', 'Американский киноактёр', '1921-11-03'),
	(62, 'Генри Фонда', 'Американский актёр театра и кино, лауреат премии «Оскар», отец актёра Питера Фонды, актрисы Джейн Фонды и дедушка актрисы Бриджит Фонды. Фонда стал известен как актёр Бродвея. Его дебют в Голливуде состоялся в 1935 году. Карьера резко пошла в гору после получения номинации на премию «Оскар» в 1941 году за роль Тома Джоуда в фильме «Гроздья гнева», поставленном по одноимённому роману Джона Стейнбека о фермерской семье из Оклахомы, которая во времена Великой депрессии в поисках лучшей жизни переезжает на Запад, попадая в Пыльный котёл.', '1905-05-16'),
	(63, 'Мэттью Макконахи', 'Американский актёр кино, телевидения и озвучивания, кинорежиссёр, сценарист и продюсер. Поначалу, зарекомендовав себя как актёр в основном комедийного амплуа, во втором десятилетии XXI века Макконахи перешёл к крупным драматическим ролям, удостоившись ряда наград и положительных отзывов от кинопрессы за картины «Линкольн для адвоката», «Мад», «Киллер Джо», «Супер Майк», «Интерстеллар».американский актёр кино, телевидения и озвучивания, кинорежиссёр, сценарист и продюсер. Поначалу, зарекомендовав себя как актёр в основном комедийного амплуа, во втором десятилетии XXI века Макконахи перешёл к крупным драматическим ролям, удостоившись ряда наград и положительных отзывов от кинопрессы за картины «Линкольн для адвоката», «Мад», «Киллер Джо», «Супер Майк», «Интерстеллар».', '1969-11-04'),
	(64, 'Энн Хэтэуэй', 'Американская актриса и певица. Обладательница премий «Эмми» (2010, за лучшее озвучивание), «Золотой глобус» (2013), Премии Гильдии киноактёров США (2013), «BAFTA» (2013) и «Оскар» (2013) за лучшую женскую роль второго плана в фильме-мюзикле «Отверженные». Фильмы с её участием заработали 6,4 млрд долларов по всему миру, и она вошла в Forbes Celebrity 100 в 2009 году.', '1982-11-12'),
	(65, 'Энтони Перкинс', 'Американский актёр, певец и режиссёр. Наиболее известен по роли Нормана Бейтса в фильме Альфреда Хичкока «Психо» (1960) и его сиквелах. Номинант на премию «Оскар» и двукратный номинант на премию «Тони».', '1932-04-04'),
	(66, 'Вера Майлз', 'Американская актриса. В 1948 году Вера Майлз стала победительницей конкурса «Мисс Канзас». После нескольких незначительных ролей в кино и на телевидении она появилась у Джона Форда в вестерне «Искатели» (1956). После этого её заметил Альфред Хичкок и предложил роли в нескольких своих фильмах, среди которых «Не тот человек» (1956) и «Психо» (1960)', '1929-08-23'),
	(67, 'Дэвид Морс', 'Американский актёр. Номинант на премии «Эмми» и «Тони».', '1953-10-11'),
	(68, 'Хамфри Богарт', 'Американский киноактёр. Американский институт киноискусства назвал Богарта лучшим актёром в истории американского кино.', '1899-12-25'),
	(69, 'Ингрид Бергман', 'Шведская и американская актриса. В рейтинге Американского института киноискусства — 100 величайших звёзд кино за 100 лет по версии AFI — занимает 4-е место. Трижды лауреат премий «Оскар» и «Давид ди Донателло», четырежды — премии «Золотой глобус», дважды — премии «Эмми», первый лауреат премии «Тони» (1947).', '1915-08-29'),
	(70, 'Чарльз Чаплин', 'Американский и английский киноактёр, сценарист, композитор, кинорежиссёр, продюсер и монтажёр, универсальный мастер кинематографа, создатель одного из самых знаменитых образов мирового кино — образа бродяги Чарли, появившегося в короткометражных комедиях, поставленных на поток в 1910-е годы на киностудии Кистоун. Чаплин активно использовал приёмы пантомимы и буффонады, хотя начиная с 1920-х годов в его творчестве стали проходить значительно более серьёзные социальные темы, чем это было в раннем периоде короткометражного кино. Начиная с апреля 1914 года Чаплин стал выступать в качестве режиссёра и автора сценария большинства фильмов с собственным участием, с 1916 года он также продюсировал фильмы, а с 1918 — писал музыку.', '1899-04-16'),
	(71, 'Вирджиния Черрилл', 'Американская актриса, наиболее известная по роли слепой цветочницы в фильме Чарли Чаплина «Огни большого города».', '1908-04-12'),
	(72, 'Франсуа Клюзе', 'Французский актёр театра, кино и телевидения, лауреат премии «Сезар» (2007).', '1955-09-21'),
	(73, 'Омар Си', 'Французский актёр , лауреат премии «Сезар» за лучшую мужскую роль. В числе известных актёрских работ Си такие фильмы, как «1+1», «Шоколад», «Люди Икс минувшего будущего».', '1978-01-20'),
	(74, 'Полетт Годдар', 'Американская актриса, номинантка на премию «Оскар» (1944). Жена Чарльза Чаплина в 1936—1942 (фактически в 1932—1940), Эриха Марии Ремарка (с 1958 года до смерти Ремарка в 1970 году).', '1910-06-03'),
	(75, 'Карен Аллен', 'Американская актриса.', '1951-09-05'),
	(76, 'Эдриен Броуди', 'Американский актёр и продюсер. Снялся более чем в 40 фильмах. Лауреат премий «Оскар» и «Сезар» (единственный американский актёр) в номинации «Лучший актёр». Всемирную известность приобрёл после роли польско-еврейского музыканта Владислава Шпильмана в фильме «Пианист». Наиболее известные кинокартины: «Тонкая красная линия» (1998), «Пианист» (2002), «Таинственный лес» (2004), «Пиджак» (2005), «Кинг-Конг» (2005), «Острые козырьки».', '1973-04-14'),
	(77, 'Грейс Келли', 'Американская актриса, с 1956 года — супруга князя Монако Ренье III, 10-я княгиня Монако, мать ныне правящего князя Альбера II. На её счету немногим более 10 фильмов, один «Оскар» и слава самой кассовой актрисы своего времени.', '1929-11-12'),
	(78, 'Телма Риттер', 'Американская актриса, обладательница премии «Тони», а также шестикратная номинантка на «Оскар». Все шесть номинаций Телма получила за роли второго плана. Ей принадлежит рекорд в данной категории по количеству номинаций (6) без побед, причём 4 из них были с интервалом в 1 год.', '1905-02-14'),
	(79, 'Арнольд Шварценеггер', 'Американский культурист, предприниматель, актёр, продюсер и политический деятель австрийского происхождения. 38-й губернатор Калифорнии (был избран на эту должность в октябре 2003 года и переизбран на второй срок в 2006 году). Обладатель ряда премий по бодибилдингу, в том числе семикратный обладатель титула «Мистер Олимпия». Организатор конкурса «Арнольд Классик».', '1947-07-30'),
	(80, 'Линда Хэмилтон', 'Американская актриса. Наиболее известна по роли Сары Коннор в серии фильмов «Терминатор», а также по ролям в фильмах «Дети кукурузы», «Кинг-Конг жив», «Пик Данте» и телесериале «Красавица и чудовище», за работу в котором она была номинирована на премию «Эмми» и две премии «Золотой глобус».', '1956-09-23'),
	(81, 'Майкл Джей Фокс', 'Канадо-американский актёр, писатель, активист, продюсер, режиссёр кино и телевидения. Начал сниматься в 1970-х годах, но наибольшую известность получил за исполнение роли Марти Макфлая в фантастической трилогии «Назад в будущее», а также за участие в сериале «Семейные узы», выходившем в эфир с 1982 по 1989 год, за роль в котором актёр выиграл три премии «Эмми», один «Золотой глобус», а также две «премии Гильдии киноактёров США».', '1961-06-09'),
	(82, 'Кристофер Ллойд ', 'Американский актёр кино и телевидения. Обладатель трёх премий «Эмми». Наиболее известен по роли доктора Эмметта Брауна в кинотрилогии «Назад в будущее», а также по фильмам «Пролетая над гнездом кукушки», «Кто подставил кролика Роджера», «Семейка Аддамс», «Трасса 60».', '1938-10-22'),
	(83, 'Майлз Теллер', 'Американский актёр. Наиболее известен по ролям в фильмах «Кроличья нора» (2010), «Захватывающее время» (2013), «Дивергент» (2014) «Одержимость» (2014), «Фантастическая четвёрка» (2015) и «Дело храбрых» (2017).', '1987-02-20'),
	(84, 'Дж. К. Симмонс', 'Американский актёр кино и телевидения. Больше всего известен по ролям доктора Шкоды в сериале «Закон и порядок», неонациста Шиллингера в тюремной драме HBO «Тюрьма Оз», Дж. Джоны Джеймсона в серии фильмов «Человек-паук» и подрывника Газа Панкейка в фильме братьев Коэнов «Игры джентльменов». Был удостоен множества наград, в том числе «Золотого глобуса», BAFTA и «Оскар» за роль дирижёра-тирана в психологической драме «Одержимость» (2014).', '1955-01-09'),
	(85, 'Рассел Кроу', 'Австралийский и американский киноактёр и режиссёр новозеландского происхождения. Лауреат премии «Оскар» 2001 года (3 номинации). Наибольшую известность получил благодаря ролям в таких фильмах, как: «Гладиатор», «Секреты Лос-Анджелеса», «Ной», «Славные парни», «Свой человек», «Хозяин морей: На краю земли», «Поезд на Юму», «Игры разума», «Отверженные», «Нокдаун» и «Мумия».', '1964-04-07'),
	(86, 'Хоакин Феникс', 'Американский актёр, продюсер, музыкант и клипмейкер. Младший брат актёра Ривера Феникса. Четырёхкратный номинант и двукратный лауреат премии «Золотой глобус», призёр Каннского кинофестиваля и обладатель кубка Вольпи Венецианского кинофестиваля за лучшую мужскую роль, лауреат музыкальной премии «Грэмми», трёхкратный номинант премии «Оскар» и обладатель за фильм «Джокер», номинант на премию Британской киноакадемии.', '1974-10-28'),
	(87, 'Хью Джекман', 'Австралийский актёр, певец и продюсер. Стал известен широкой публике по роли супергероя-мутанта Росомахи в серии фильмов «Люди Икс». Вершиной его актёрского мастерства подавляющим большинством критиков признана работа в детективном триллере «Пленницы» (2013). Также широко известен по фильмам «Кейт и Лео», «Пароль „Рыба-меч“», «Ван Хельсинг», «Фонтан», «Престиж», «Живая сталь», «Отверженные», «Робот по имени Чаппи» и «Австралия».', '1968-10-12'),
	(88, 'Скарлетт Йоханссон', 'Американская актриса кино и телевидения, певица. Наибольшую известность ей принесло сотрудничество с Вуди Алленом («Матч-пойнт») и Софией Копполой («Трудности перевода»), а также роль Чёрной вдовы в серии фильмов кинематографической вселенной Marvel.', '1984-11-22'),
	(89, 'Гай Пирс', 'Британский и австралийский актёр театра, кино и телевидения, музыкант, родился в Великобритании, но вырос и живёт в Австралии. Лауреат телевизионной премии «Эмми» (2011), номинант на премию «Золотой глобус» (2012). Наиболее известен по роли детектива Эдмунда «Эда» Эксли в фильме «Секреты Лос-Анджелеса», и по роли Леонарда Шелби в фильме «Помни», а также по фильмам «Я соблазнила Энди Уорхола», «Король говорит!», «Прометей», «Машина времени», «Самый пьяный округ в мире», «Железный человек 3» и по сериалу «Милдред Пирс».', '1967-10-05'),
	(90, 'Джо Пантолиано', 'Американский актёр, продюсер, режиссёр, сценарист, обладатель премии «Эмми»[4]. Прославился исполнением ролей «мерзких типов» — предателя Сайфера в фильме «Матрица» (1999), детектива Тедди в «Помни», Ральфа Сифаретто в «Клан Сопрано» и других. Снялся более чем в 100 фильмах и сериалах, включая «Чёртова служба в госпитале Мэш» и «Полиция Нью-Йорка».', '1951-09-12'),
	(91, 'Мартин Шин', 'Американский актёр и кинорежиссёр, известный в частности как исполнитель главной роли в знаменитом фильме Фрэнсиса Форда Копполы «Апокалипсис сегодня». Он работал с такими кинорежиссёрами как Ричард Аттенборо, Фрэнсис Форд Коппола, Терренс Малик, Майк Николс, Мартин Скорсезе, Стивен Спилберг, Оливер Стоун и Роб Райнер. Лауреат премий «Эмми» и «Золотой глобус».', '1940-08-03'),
	(92, 'Том Скерритт', 'Американский актёр. Лауреат премии «Эмми», а также номинант на два «Золотых глобуса».', '1933-08-25'),
	(93, 'Сигурни Уивер', 'Американская актриса и продюсер. Приобрела широкую известность после исполнения роли Эллен Рипли в фильмах «Чужой» (1979), «Чужие» (1986), «Чужой 3» (1992) и «Чужой: Воскрешение» (1997). Эта роль принесла ей множество наград, в том числе номинацию на премию «Оскар», а героиня актрисы Эллен Рипли стала одним из самых значительных женских героев в мировом кино. Сигурни Уивер считается ветераном экшн-героинь в научно-фантастических фильмах.', '1949-10-08'),
	(94, 'Вероника Картрайт', 'Британская актриса, которая снимается главным образом в американских фильмах и на телевидении. Наиболее известна как штурман Ламберт в «Чужом», за роль которой она была удостоена премии «Сатурн».', '1949-04-20'),
	(95, 'Уильям Холден', 'Американский актёр, лауреат премии «Оскар» за главную роль в фильме «Лагерь для военнопленных № 17» (1953). Шесть раз его имя называли в десятке самых популярных актёров США (1954—1958, 1960). Американский институт киноискусства поместил Уильяма Холдена на 25-е место в списке «100 величайших звёзд кино».', '1918-04-17'),
	(96, 'Глория Свенсон', 'Американская актриса, продюсер, икона стиля своего времени и одна из самых ярких и окупаемых[8] звезд эпохи немого кино, актриса театра и телевидения. Современному зрителю наиболее известна благодаря исполнению главной роли в классическом фильме-нуар Билли Уайлдера «Бульвар Сансет».', '1899-03-27'),
	(97, 'Питер Селлерс', 'Английский актёр. Наибольшую и общемировую известность ему принёс образ придурковатого инспектора Клузо в фильме Блейка Эдвардса «Розовая пантера» 1963 года и его продолжениях.', '1925-09-08'),
	(98, 'Джордж К. Скотт', 'Американский актёр, режиссёр и продюсер, обладатель премии «Оскар» и первый актёр, отказавшийся от этой премии. Скотт был очень популярен благодаря ролям на театральной сцене и успехам в кино, запомнившись по работе в фильмах «Мошенник», «Доктор Стрейнджлав, или Как я перестал бояться и полюбил бомбу» и «Паттон».', '1927-10-18'),
	(99, 'Филипп Нуаре', 'Французский актёр театра и кино. Обладатель двух премий «Сезар» за лучшую мужскую роль в картинах «Старое ружьё» (1976) и «Жизнь и ничего больше» (1990). Премия BAFTA за лучшую мужскую роль в фильме «Новый кинотеатр „Парадизо“» (1991). Кавалер ордена Почётного легиона (2005).', '1930-10-01'),
	(100, 'Жак Перрен', 'Французский киноактёр и продюсер.', '1941-07-13'),
	(101, 'Ульрих Мюэ', 'Немецкий актёр театра и кино. Наибольший успех ему принесла главная роль в фильме «Жизнь других», который в 2007 году был удостоен премии «Оскар» как лучший фильм на иностранном языке.', '1953-06-20'),
	(102, 'Мартина Гедек', 'Немецкая актриса, ставшая всемирно известной благодаря фильму «Жизнь других» (2006).', '1961-09-14'),
	(103, 'Себастьян Кох', 'Немецкий актёр. С начала 2000-х сыграл множество разноплановых киноролей, благодаря чему приобрёл общеевропейскую известность.', '1962-05-31'),
	(105, 'Кирк Дуглас', 'Американский актёр, звёздный актёр «золотой эры» Голливуда. Кроме того, писатель, филантроп и бывший посол доброй воли Госдепартамента США. В отличие от не менее знаменитого сына, Майкла Дугласа, долгая и успешная карьера в кино Кирка была отмечена только почётным «Оскаром» в 1996 году. Обладатель премии «Золотой глобус» за роль Винсента Ван Гога в биографической ленте «Жажда жизни».', '1916-12-09'),
	(106, 'Джордж Макреди', 'Американский актёр театра, кино и телевидения, более всего известный своими ролями лощёных злодеев в фильмах 1940-50-х годов. Один из тех характерных актёров, который лучше других умел передать ощущение угрозы, Макреди за время своей карьеры, охватившей четыре десятилетия, сыграл более чем в 60 художественных фильмах, 100 театральных постановках и 250 эпизодах телесериалов. Свои наиболее значимые роли в кино Макреди исполнил в фильмах нуар «Меня зовут Джулия Росс» (1945), «Гильда» (1946), «Большие часы» (1948), «Стучись в любую дверь» (1949) и «Детективная история» (1951), а также антивоенной драме Стенли Кубрика «Тропы славы» (1957).', '1899-12-29'),
	(107, 'Джейми Фокс', 'Американский актёр и певец, участник stand-up comedy. Стал широко известен благодаря роли Рэя Чарльза в биографическом фильме «Рэй». Сыграв в «Рэе», стал третьим афроамериканцем, удостоившимся премии «Оскар». Известен также по ролям в фильмах «Соучастник», «Законопослушный гражданин», «Джанго освобожденный», «Штурм Белого дома», «Новый Человек-паук. Высокое напряжение», «Али».', '1967-12-13'),
	(108, 'Кристоф Вальц', 'Австрийский актёр, получивший широкую известность благодаря роли штандартенфюрера СС Ганса Ланды в фильме Квентина Тарантино «Бесславные ублюдки». За эту же роль был удостоен всевозможных кинопремий, включая «Оскар», «Золотой глобус», BAFTA и приз Каннского кинофестиваля. Позже продолжил сотрудничество с Тарантино, сыграв в его спагетти-вестерне «Джанго освобождённый». Это перевоплощение было отмечено вторыми премиями «Оскар», «Золотой глобус» и BAFTA.', '1956-10-04'),
	(109, 'Шелли Дюваль', 'Американская актриса кино и телевидения, номинантка на премии BAFTA (1978) и «Эмми» (1988, 1992), обладательница приза за лучшую женскую роль на Каннском кинофестивале (1977). Наиболее известна по участию в фильмах «Нэшвилл» (1975), «Три женщины» (1977), «Энни Холл» (1977), «Сияние» (1980), «Попай» (1980), «Бандиты времени» (1981) и «Портрет леди» (1996).', '1949-07-07'),
	(110, 'Аннетт Бенинг', 'Американская актриса. Обладательница премий «Золотой глобус» и BAFTA, а также четырёхкратная номинантка на премию «Оскар» (1991, 2000, 2005, 2011). Наиболее известна по участию в фильмах «Багси», «Любовная история», «Марс атакует!», «Вальмон», «Красота по-американски», «Театр», «Женщины» и «Детки в порядке».', '1958-05-29'),
	(111, 'Майкл Бин', 'Американский актёр, известный по фантастическим боевикам Джеймса Кэмерона («Терминатор», «Чужие» и «Бездна»).', '1956-07-31'),
	(112, 'Чхве Мин Сик', 'Южнокорейский актёр.', '1962-04-27'),
	(113, 'Кан Хё Чжон', 'Южнокорейская актриса и фотомодель.', '1982-01-04'),
	(114, 'Чарльз Лоутон', 'Британский и американский актёр и режиссёр, обладатель премии «Оскар» за лучшую мужскую роль в картине «Частная жизнь Генриха VIII».', '1899-07-01'),
	(115, 'Тайрон Пауэр', 'Американский актёр, наиболее известный своими романтическими ролями в классических голливудских фильмах 1930—1950-х годов.', '1914-05-05'),
	(116, 'Марлен Дитрих', 'Немецкая и американская актриса, певица, секс-символ. Она ещё при жизни стала легендой, создав в кино один из совершенных женских образов и оказав большое влияние на моду, стиль и женское сознание. Дитрих представляла собой шокирующий тип прекрасной и обаятельной женщины, одновременно порочной и невинной, вызывающе сексуальной и неприступной. Её судьба, замешанная на искусстве, политике и сексе, её одиночество в конце жизни все ещё вызывают интерес и споры. Дитрих осталась в истории и как популярная певица. Её жестковатое контральто с выразительным тембром до сих пор привлекает поклонников своим волнующим обаянием. В 1999 году Американский институт киноискусства поставил Марлен Дитрих на девятое место в списке величайших актрис классического голливудского кино.', '1901-12-27'),
	(117, 'Юрген Прохнов', 'Немецкий киноактёр. Его наиболее известными в других странах ролями являются роль капитана подлодки в фильме «Подводная лодка», герцога Лето Атрейдеса в фильме «Дюна» и злодея Максвелла Дента в фильме «Полицейский из Беверли-Хиллз 2».', '1941-06-10'),
	(118, 'Орсон Уэллс', 'Американский кинорежиссёр, актёр, сценарист, который работал в театре, на радио и в кино. Его фильмы отмечены изобретательностью в технических решениях. Наиболее примечательные картины режиссёра — «Гражданин Кейн», «Печать зла», «Процесс» и «Леди из Шанхая». Уэллс занимает 16-ю строку в списке Американского института киноискусства среди величайших мужчин-легенд экрана классического голливудского кино.', '1915-05-06'),
	(119, 'Аамир Xан', 'Индийский актёр, продюсер, режиссёр, сценарист и телеведущий. Снимается в фильмах на хинди и зарекомендовал себя как один из самых популярных и влиятельных актёров индийского кино. Обладатель ряда номинаций и кинопремий, в том числе Filmfare Awards и Национальной кинопремии Индии. Был удостоен правительственных наград Падма Шри в 2003 году и Падма Бхушан в 2010.', '1965-03-14'),
	(120, 'Ким Новак', 'Американская актриса, наиболее известная своей ролью в триллере Альфреда Хичкока «Головокружение» (1958).', '1933-02-13'),
	(121, 'Кэри Грант', 'Англо-американский актёр и артист водевилей. Икона популярной культуры, кинозвезда. В 1942 году стал натурализированным гражданином Соединённых Штатов. В 1999 году Американский институт киноискусства поместил Гранта на вторую строчку в рейтинге «величайших актёров Голливуда» (после Хамфри Богарта).', '1904-01-18'),
	(122, 'Эва Мари Сейнт', 'Американская актриса, обладательница премии «Оскар» (1955) за лучшую женскую роль второго плана, одна из восьми актрис в истории мирового кинематографа, получивших эту награду за дебютную роль второго плана.', '1924-07-04'), 
	(123, 'Мел Гибсон', 'Австралийский и американский актёр кино и телевидения, кинорежиссёр, сценарист и продюсер. Лауреат двух премий «Оскар» и премии «Золотой глобус» (1996) за режиссуру драмы «Храброе сердце». В 2017 году был номинирован на премии «Оскар» и «Золотой глобус» за режиссуру военной драмы «По соображениям совести». Почётный офицер ордена Австралии.', '1956-01-03'),
	(124, 'Софи Марсо', 'Французская киноактриса, кинорежиссёр и певица.', '1966-11-17'),
	(125, 'Харви Кейтель', 'Американский актёр и продюсер. Номинант на премии «Оскар» и «Золотой глобус» за роль гангстера Микки Коэна в фильме «Багси». Наиболее известен по участию в фильмах Мартина Скорсезе и Квентина Тарантино, таких как «Таксист», «Злые улицы», «Бешеные псы», «Криминальное чтиво», а также «От заката до рассвета». Вместе с актерами Алем Пачино и Эллен Бёрстин Кейтель является нынешним сопрезидентом Актёрской Студии.', '1939-05-13'),
	(126, 'Тим Рот', 'Британский актёр, кинорежиссёр и кинопродюсер. Номинант на премии «Оскар» и «Золотой глобус», номинант и обладатель премии BAFTA. Наиболее знаменит своей дружбой и многолетним сотрудничеством с кинорежиссёром Квентином Тарантино, у которого он сыграл свои самые знаменитые роли в таких кинокартинах, как «Бешеные псы», «Криминальное чтиво», «Четыре комнаты» и «Омерзительная восьмёрка», а также по фильмам «Легенда о пианисте», «Розенкранц и Гильденстерн мертвы» и по сериалу «Обмани меня».', '1961-05-14'),
	(127, 'Стив Бушеми', 'Американский актёр, режиссёр и бывший пожарный. Лауреат премий «Эмми» и «Золотой глобус». Известность Бушеми принесли такие фильмы, как «Прощальные взгляды» (1986), «Нью-Йоркские истории» (1989), «Таинственный поезд» (1989), «Бешеные псы» (1992), «Отчаянный» (1995), «Воздушная тюрьма» (1997), «Армагеддон» (1998), «Серая зона» (2001), «Призрачный мир» (2001), «Крупная рыба» (2003) и «Смерть Сталина» (2017). Он также известен по сотрудничеству с братьями Коэн, у которых снялся в фильмах «Перекрёсток Миллера» (1990), «Бартон Финк» (1991), «Подручный Хадсакера» (1994), «Фарго» (1996) и «Большой Лебовски» (1998).', '1957-12-13'),
	(128, 'Петер Лорре', 'Австрийский и американский актёр театра и кино, режиссёр, сценарист.', '1904-06-26'),
	(129, 'Эллен Бёрстин', 'Американская актриса театра, кино и телевидения. Лауреат премий «Оскар» (1975), BAFTA (1975), «Золотой глобус» (1979), «Тони» (1975), «Джини» (2008) и двух премий «Эмми» (2009, 2013). Актёрскую карьеру начала в 1958 году, сыграв небольшую роль в телесериале «Телевизионный театр Крафта» (1958). Первой её заметной работой в кино стала роль Лоиз Фэрроу в фильме «Последний киносеанс» в 1971 году, за свою роль актриса была номинирована на премии «Оскар» и «Золотой глобус». В 1973 году Эллен Бёрстин снялась в фильме ужасов «Изгоняющий дьявола», эта роль также принесла ей номинации на премии «Оскар» и «Золотой глобус».', '1932-12-07'),
	(130, 'Джаред Лето', 'Американский рок-музыкант и актёр, вокалист альтернативной рок-группы Thirty Seconds to Mars. Является режиссёром многих музыкальных клипов под псевдонимом Bartholomew Cubbins (имя заимствовано из книги Доктора Сьюза «500 шляп Варфоломея Куббинса»). Джаред Лето — обладатель множества наград, в том числе премии «Оскар», «Золотого глобуса» и премии Гильдии киноактёров США за роль трансгендерной женщины в драме «Далласский клуб покупателей» (2013).', '1971-12-26'),
	(131, 'Дженнифер Коннелли', 'Американская актриса. В кино снимается с детства, но известности добилась с выходом фильмов «Тёмный город» (1998), «Реквием по мечте» (2000) и «Игры разума» (2001), за последний из которых она получила премию «Оскар» в номинации «Лучшая женская роль второго плана». Она также известна своими ролями в таких картинах, как «Халк» (2003), «Дом из песка и тумана» (2003), «Кровавый алмаз» (2006), «Как малые дети» (2006), «День, когда Земля остановилась» (2008) и «Обещать — не значит жениться» (2009).', '1970-12-12'),
	(132, 'Одри Тоту', 'Французская актриса театра и кино.', '1976-08-09'),
	(133, 'Матьё Кассовиц', 'Французский кинорежиссёр, сценарист, актёр.', '1967-08-03'),
	(134, 'Финн Уайтхед', 'Британский актёр кино и телевидения, получивший известность после появления в фильме Кристофера Нолана «Дюнкерк».', '1997-07-18'),
	(135, 'Том Харди', 'Английский актёр театра и кино, продюсер и сценарист. Обладатель премии BAFTA и номинант на премию «Оскар». Прославился благодаря фильму «Стюарт: Прошлая жизнь», где он сыграл роль наркомана и бездомного Стюарта Шортера, принёсшую ему номинацию на премию BAFTA. Также известен по своим главным и второстепенным ролям в таких фильмах, как «Бронсон», «Рок-н-рольщик», «Начало», «Воин», «Легенда», «Общак», «Самый пьяный округ в мире», «Тёмный рыцарь: Возрождение легенды», «Лок», «Безумный Макс: Дорога ярости», «Выживший», «Дюнкерк» и «Веном». Командор ордена Британской империи (CBE, 2018).', '1977-09-15'),
	(136, 'Киллиан Мерфи', 'Ирландский актёр театра и кино, музыкант. Часто отмечается критиками за «хамелеонские» роли и выразительные голубые глаза.', '1976-05-25'),
	(137, 'Даршил Сафари', 'Индийский актёр. Его дебютный фильм — «Звёздочки на земле», где он сыграл мальчика, страдающего дислексией. Сейчас Даршил — студент H.R. College of Commerce and Economics в Мумбаи. Его отец, Митеш Сафари, в 1990 году сыграл в эпическом сериале Chanakya, главного героя Чанакью в юности.', '1997-03-09'),
	(138, 'Малкольм Макдауэлл', 'Британский актёр театра, кино, телевидения и озвучивания с более чем сорокалетней карьерой, получивший широкую известность своими яркими, порой жестокими и резко отрицательными ролями. Проходил подготовку в Лондонской академии музыкального и драматического искусства. Номинант на премию «Золотой глобус» в категории «Лучшая мужская роль — драма» за роль в фильме «Заводной апельсин» (1971). Также в 1980 году актёр получил номинацию на премию «Сатурн» в категории «Лучший киноактёр» за роль в фильме «Путешествие в машине времени» (1979).', '1943-06-13'),
	(139, 'Патрик Мэги', 'Ирландский актёр и театральный режиссёр знаменитый главным образом благодаря ролям в картинах Стэнли Кубрика «Заводной апельсин» и «Барри Линдон», а также сотрудничеству с театральными режиссёрами Гарольдом Пинтером и Сэмюэлом Беккетом.', '1924-03-31'),
	(140, 'Питер О’Тул', 'Британский актёр ирландского происхождения. Имел больше всех номинаций на «Оскар» (восемь) за актёрскую работу, но без единой победы. За свою полувековую карьеру был удостоен четырёх «Золотых глобусов» (дважды как лучший драматический актёр), премий BAFTA и «Эмми».', '1932-08-02'),
	(141, 'Омар Шариф', 'Египетский актёр. Известен главным образом по работам в американском и европейском кино. Двукратный обладатель премии «Золотой глобус» (1963, 1966) и номинант на премию «Оскар» (1963).', '1932-04-10'),
	(142, 'Алек Гиннесс', 'Британский актёр театра и кино. В 1958 году был удостоен премии «Оскар» за лучшую мужскую роль в военном фильме «Мост через реку Квай», а спустя два десятилетия Гиннессу была вручена почётная статуэтка за жизненный вклад в мировое киноискусство в течение долгой и успешной актёрской карьеры.', '1914-04-02'),
	(143, 'Барбара Стэнвик', 'Американская актриса, модель и танцовщица, которая была особенно популярна в 1930—1940-х годах.', '1907-07-16'),
	(144, 'Фред Макмюррей', 'Американский актёр.', '1908-08-30'),
	(145, 'Эдвард Г. Робинсон', 'Американский актёр. Американский институт киноискусства поместил его на 24-е место в списке «100 величайших звёзд кино».', '1893-12-12'),
	(146, 'Сибилл Шеперд', 'Американская актриса, певица и бывшая модель. Обладательница трёх премий «Золотой глобус», а также четырёхкратная номинантка на премию «Эмми». Наибольшую известность ей принесли роли в фильмах «Последний киносеанс» (1971), «Разбивающий сердца» (1972) и «Таксист» (1976), а также сериалах «Детективное агентство „Лунный свет“» (1985—1989), «Сибилл» (1995–1998), «Секс в другом городе» (2007—2009) и «Ясновидец» (2008—2013).', '1950-02-18'),
	(147, 'Ф. Мюррей Абрахам', 'Американский актёр. Лауреат премий «Оскар» и «Золотой глобус» за роль Антонио Сальери в фильме «Амадей» (1984). Наиболее известен по ролям в фильмах «Лицо со шрамом» (1983) и «Найти Форрестера» (2000), а также в сериале «Родина» (2012—2018).', '1939-10-24'),
	(148, 'Том Халс', 'Американский актёр, певец и продюсер. Лауреат премий «Эмми» и «Тони», а также номинант на «Оскар» и «Золотой глобус».', '1953-12-06'),
	(149, 'Джим Керри', 'Канадо-американский актёр, комик, сценарист, продюсер и художник. Наиболее известен по фильмам «Маска», «Бэтмен навсегда», «Эйс Вентура: Розыск домашних животных», «Эйс Вентура: Когда зовёт природа», «Тупой и ещё тупее», «Лжец, лжец», «Брюс Всемогущий», «Шоу Трумана», «Человек на луне», «Вечное сияние чистого разума». Обладатель двух и номинант на шесть «Золотых глобусов», номинант на премию BAFTA, а также обладатель ряда других премий. Является одним из самых высокооплачиваемых комиков США и мира.', '1962-02-17'),
	(150, 'Кейт Уинслет', 'Британская актриса. Актёрскую карьеру Кейт Уинслет начала в 1991 году, дебютировав на британском телевидении. Дебютом в кино для Уинслет стала картина «Небесные создания» (1994), получившая признание кинокритиков. Всемирная слава к ней пришла спустя всего несколько лет, после исполнения роли Розы Дьюитт Бьюкейтер в фильме-катастрофе «Титаник» (1997), за которую она получила свою вторую номинацию на премию «Оскар». В дальнейшем она была отмечена в таких фильмах, как «Перо маркиза де Сада» (2000), «Айрис» (2001), «Вечное сияние чистого разума» (2004), «Волшебная страна» (2004), «Как малые дети» (2006), «Чтец» (2008), «Дорога перемен» (2008) и «Стив Джобс» (2015), за которые была номинирована на различные кинонаграды.', '1975-10-05'),
	(151, 'Грегори Пек', 'Американский актёр, одна из наиболее востребованных голливудских звёзд 1940—1960 годов. Лауреат премии «Оскар» в номинации «Лучший актёр» за роль адвоката Аттикуса Финча в драме «Убить пересмешника» (1962). В 1999 году Пек занял двенадцатую строчку в списке 100 величайших киноактёров в истории по версии Американского института киноискусства.', '1916-04-05'),
	(152, 'Мэттью Модайн', 'Американский актёр, наиболее известный ролями в фильмах «Цельнометаллическая оболочка» С. Кубрика и «Птаха» А. Паркера.', '1959-03-22'),
	(153, 'Винсент Д`Онофрио', 'Американский актёр и продюсер.', '1959-06-30'),
	(154, 'Кир Дулли', 'Американский актёр кино и телевидения, наиболее известный по роли астронавта Дэйва Боумена в фильме «Космическая одиссея 2001 года», а также в сиквеле этой ленты «2010: год вступления в контакт».', '1936-05-30'),
	(155, 'Гари Локвуд', 'Американский актёр, сыгравший одну из главных ролей в фильме «Космическая одиссея 2001 года», а также в одном из эпизодов в сериале «Звёздный путь».', '1937-02-21'),
	(156, 'Джин Келли', 'Американский киноактёр, кинорежиссёр, сценарист, продюсер, хореограф и певец. Обладатель внеконкурсного почётного «Оскара» 1952 года со следующей формулировкой своих заслуг — «в знак признания его универсального таланта актёра, певца, режиссёра и танцовщика и особенно за его блестящие достижения в искусстве кинохореографии». Известен прежде всего своей ролью в знаменитом мюзикле «Поющие под дождём», хотя он был звездой Голливуда в 1940-е и 1950-е годы, создав за это время десятки замечательных фильмов, вошедших в золотой фонд мирового кинематографа.', '1912-08-23'),
	(157, 'Дональд О’Коннор', 'Американский актёр, певец, танцор, телеведущий, композитор, режиссёр и сценарист. В странах СНГ он известен прежде всего благодаря второстепенной роли в культовом фильме «Поющие под дождём» (1952). За роль в этой картине О’Коннор стал лауреатом премии «Золотой глобус» в категории «Лучший актер комедии или мюзикла» в 1953 году. За свой вклад в кинематограф и телевидение, О’Коннор был удостовен двух звёзд на голливудской «Аллее славы».', '1925-08-28'),
	(158, 'Дебби Рейнольдс', 'Американская актриса и певица. В отличие от не менее знаменитой дочери, Кэрри Фишер, долгая и успешная карьера в кино Рейнольдс была отмечена только наградой имени Джина Хершолта в 2016 году.', '1932-04-01'),
	(159, 'Роберт Редфорд', 'американский актёр, кинорежиссёр, продюсер и бизнесмен. Основатель кинофестиваля «Сандэнс». Лауреат премии «Оскар» за лучшую режиссуру — является одним из шести режиссёров в истории мирового кинематографа, получивших эту награду за дебютный фильм.', '1936-08-18'),
	(160, 'Пол Ньюман', 'Американский актёр, кинорежиссёр, продюсер, которого называют одним из столпов Голливуда. Десять раз выдвигался на премию «Оскар», из них восемь — в номинации за лучшую мужскую роль. Бизнесмен и общественный деятель, собрал и пожертвовал более 250 миллионов долларов на образование и благотворительность как владелец-основатель крупной продовольственной фирмы «Newman’s Own».', '1925-01-26'),
	(161, 'Роберт Шоу', 'Британский киноактёр, сценарист и писатель.', '1927-08-09'),
	(162, 'Р. Мадхаван', 'Индийский актёр, сценарист и продюсер. Один из немногих актеров в Индии, который смог достичь всеиндийской известности, появившись в фильмах на семи разных языках.', '1970-06-01'),
	(163, 'Шарман Джоши', 'Индийский актёр кино и театра. Участвовал во многих постановках на английском, хинди, маратхи и гуджарати, но именно наибольшую известность принесли ему фильмы на хинди.', '1979-04-28'),
	(164, 'Ламберто Маджорани', 'Итальянский актёр. Ламберто Маджорани не имел актёрского образования. В кино он попал благодаря тому, что создатели фильма «Похитители велосипедов» ратовали за участие актёров-непрофессионалов для обеспечения реализма: режиссёр картины Витторио Де Сика в поисках актёра на роль Антонио Риччи посетил завод «Бреда», где Ламберто работал токарем, и пригласил рабочего на пробу. Проба оказалась успешной, и Де Сика предложил Маджорани сыграть эту роль, взяв с него слово, что по истечении съемочного периода тот вернётся на завод. Однако в процессе съёмок на заводе произошло сокращение, и Ламберто был уволен.', '1909-04-22'),
	(165, 'Майк Майерс', 'Канадский актёр, комик, сценарист и продюсер фильмов. Больше всего прославился, сыграв Уэйна Кэмпбелла в фильме «Мир Уэйна», а также Остина Пауэрса и Доктора Зло в трилогии «Остин Пауэрс».', '1963-05-25'),
	(166, 'Элай Рот', 'Американский киноактёр, кинорежиссёр, сценарист и продюсер. Он известен как человек, вернувший фильмы с рейтингом R, в то время как студии сконцентрировали внимание на фильмах категории PG-13.', '1972-04-18'),
	(167, 'Джеки Куган', 'Американский киноактёр. Вошёл в историю кинематографа, как первый в истории ребёнок-кинозвезда. С ранних лет выходил на сцену вместе с отцом и снимался в немых фильмах. Сотрудничал с Чарли Чаплином. Восхождение на вершину славы началось для Джеки с роли маленького мальчика в знаменитом фильме «Малыш» (1921), после чего интерес к нему как к актёру рос с каждым годом.', '1914-10-26'),
	(168, 'Джейсон Стейтем', 'Английский актёр, известный по фильмам режиссёра Гая Ричи «Карты, деньги, два ствола», «Большой куш» и «Револьвер», а также дилогии «Адреналин» и сериям фильмов «Перевозчик», «Неудержимые» и «Форсаж» (начиная с 6-го фильма).', '1967-07-26'),
	(169, 'Бенисио дель Торо', 'Пуэрто-риканский актёр.', '1967-02-19'),
	(170, 'Раде Шербеджия', 'Югославский и хорватский актер сербского происхождения, снимавшийся во множестве фильмов, как у себя на родине, так и на Западе; с 1990-х годов начал активно работать в Голливуде, преимущественно играя роли восточноевропейцев (Милич в «С широко закрытыми глазами» Стэнли Кубрика, русский мафиози Борис «Бритва» Юринов в «Большом куше», биохимик Владимир Нехорович в «Миссия невыполнима 2», олигарх Иван Третьяк в «Святом», генерал-лейтенант Востов в «Космических ковбоях», болгарский волшебник Грегорович в «Гарри Поттере и Дарах Смерти», словак Тибор в «Евротуре», советский генерал Дмитрий Греденко в телесериале «24 часа», биолог Александр Сирко в телесериале «Поверхность», русский генерал в «Люди Икс: Первый класс»).', '1946-07-27'),
	(171, 'Винни Джонс', 'Британский актёр, в прошлом — футболист, выступавший на позиции полузащитника.', '1965-01-05'),
	(172, 'Деннис Фарина', 'Американский актёр.', '1944-02-29');
