INSERT INTO producer (id, full_name, description, birthday_at) values
 	(1, 'Фрэнк Дарабонт', 'Американский кинорежиссёр, сценарист и продюсер венгерского происхождения. Был трижды номинирован на «Оскар». Известен своими удачными экранизациями романов Стивена Кинга: «Побег из Шоушенка», «Зелёная миля», «Мгла» и другие.', '1959-01-28'),
 	(2, 'Фрэнсис Форд Коппола', 'Американский кинорежиссёр, сценарист и продюсер. Наиболее известен благодаря своей кинотрилогии «Крёстный отец» и фильму о вьетнамской войне «Апокалипсис сегодня». Брат Талии Шайр, дядя Николаса Кейджа и Джейсона Шварцмана, отец Софии Копполы и Романа Форда Копполы. Фрэнсис является шестикратным лауреатом премии «Оскар», в том числе «за выдающийся продюсерский вклад в развитие кинопроизводства». Три золотых статуэтки Коппола получил за сценарии своих фильмов, одну — в номинации «лучший фильм» и ещё одну — за «лучшую режиссуру». Кроме того, Коппола является двукратным обладателем «Золотой пальмовой ветви», за фильмы «Разговор» и «Апокалипсис сегодня», и лауреатом множества других кинематографических наград и номинаций.', '1939-04-07'),
 	(3, 'Кристофер Нолан', 'Британский и американский кинорежиссёр, сценарист и продюсер. Является одним из самых кассовых режиссёров в истории, а также одним из самых известных и влиятельных кинематографистов своего времени. Приобрёл популярность и хорошие отзывы критиков благодаря трилогии о Тёмном Рыцаре (2005—2012); «Началу» (2010), за который он получил номинацию на премию «Оскар» за лучший фильм и вторую номинацию за оригинальный сценарий; «Интерстеллар» (2014) и «Дюнкерку» (2017), который принес ему первую номинацию на премию «Оскар» за лучшую режиссуру.', '1970-07-30'),
 	(4, 'Сидни Люмет', 'Американский кинорежиссёр, сценарист и кинопродюсер. Один из самых плодовитых и выдающихся голливудских кинорежиссёров послевоенного времени, в течение полувека снимавший в среднем более одного фильма в год. Вершиной его режиссёрского мастерства считаются работы, вышедшие в 1970-х годах — в основном, «Собачий полдень» и «Телесеть».', '1924-06-25'),
 	(5, 'Стивен Спилберг', 'Американский кинорежиссёр, сценарист, продюсер и монтажёр, является одним из самых успешных кинорежиссёров США в истории. Его 20 самых кассовых картин собрали в прокате 10 млрд долларов. Трёхкратный лауреат премии «Оскар». Постановщик знаковых картин «Челюсти», «Инопланетянин», «Список Шиндлера», «Спасти рядового Райана», «Линкольн», а также серии фильмов об археологе Индиане Джонсе и двух фильмов «Парк юрского периода».', '1946-12-18'),
 	(6, 'Квентин Тарантино', 'Американский кинорежиссёр, сценарист, актёр, кинопродюсер и кинооператор. Один из наиболее ярких представителей постмодернизма в кинематографе. Фильмы Тарантино отличаются нелинейной структурой повествования, переосмыслением культурно-исторического процесса, использованием готовых форм и эстетизацией насилия. Мировую известность получил после картины «Криминальное чтиво» (1994), которая принесла ему «Золотую пальмовую ветвь» Каннского кинофестиваля, а также премии «Оскар», «BAFTA» и «Золотой глобус» за лучший сценарий.', '1963-03-27'),
 	(7, 'Питер Джексон', 'Новозеландский кинорежиссёр, сценарист, актёр и продюсер, ставший всемирно известным после выхода на экраны кинотрилогии, поставленной им по произведению Дж. Р. Р. Толкина «Властелин колец» и «Хоббит». Награждён тремя премиями «Оскар», включая награду лучшему режиссёру; также является обладателем премий «Золотой глобус», BAFTA и «Сатурн» за лучшую режиссёрскую работу. Снял одни из самых кассовых художественных фильмов в истории кинематографа — «Властелин колец: Возвращение короля» и «Хоббит: Нежданное путешествие» собрали в прокате свыше миллиарда долларов каждый. В преодолении миллиардного барьера является вторым режиссёром после Джеймса Кэмерона.', '1961-10-31'),
 	(8, 'Серджо Леоне', 'Итальянский кинорежиссёр, сценарист, продюсер. Известен как один из основателей жанра спагетти-вестерн.', '1929-01-03'),
 	(9, 'Дэвид Финчер', 'Американский кинорежиссёр, продюсер и клипмейкер, один из самых ярких «новобранцев» Голливуда 1990-х. Лауреат премий «Золотой глобус» и BAFTA (2011) за постановку фильма «Социальная сеть» и «Эмми» (2013) за режиссуру пилотного эпизода сериала «Карточный домик», а также двукратный номинант на премию «Оскар».', '1962-08-28'),
 	(10, 'Роберт Земекис', 'Американский кинорежиссёр, сценарист и продюсер. Лауреат премий «Оскар» и «Золотой глобус» в категории «Лучший режиссёр». Постановщик знаковых картин: «Форрест Гамп», «Контакт», «Изгой», «Кто подставил кролика Роджера» и трилогии «Назад в будущее».', '1952-05-14'),
 	(11, 'Ирвин Кершнер', 'Американский кинорежиссёр, актёр и продюсер. Больше всего известен, как постановщик фильмов «Звёздные войны. Эпизод V: Империя наносит ответный удар», «Никогда не говори „никогда“» и «Робокоп 2».', '1923-04-29'),
 	(12, 'Милош Форман', 'Чешский и американский кинорежиссёр и сценарист. Дважды лауреат премии «Оскар» за лучшую режиссёрскую работу. Постановщик таких знаковых картин, как «Пролетая над гнездом кукушки», «Амадей», «Вальмон» и других.', '1932-02-18'),
 	(13, 'Мартин Скорсезе', 'Американский кинорежиссёр, продюсер, сценарист и актёр. Фильмам Скорсезе присущи выразительная жестокость и насилие, в кинематографических кругах он известен как мастер гангстерских лент.', '1942-11-17'),
 	(14, 'Лана Вачовски', 'Американский режиссёр, продюсер и сценарист. Является транс-женщиной. Вачовски дебютировала в режиссуре с фильмом «Связь» (1996), и получила мировую известность после создания фантастической трилогии «Матрица».', '1965-06-21'),
 	(15, 'Лилли Вачовски', 'Американский режиссёр, продюсер и сценарист. Является транс-женщиной. Вачовски дебютировала в режиссуре с фильмом «Связь» (1996), и получила мировую известность после создания фантастической трилогии «Матрица».', '1967-12-29'),
 	(16, 'Акира Куросава', 'Японский кинорежиссёр, сценарист и продюсер. Считается одним из самых влиятельных кинорежиссёров за всю историю кино. Куросава создал 30 фильмов в течение 57 лет своей творческой деятельности. Акира Куросава по праву считается классиком японского кино, чьё творчество оказало огромное влияние не только на национальный, но и на мировой кинематограф. Причём за рубежом он всегда пользовался большей популярностью, чем у себя на родине.', '1910-03-23'),
 	(17, 'Джордж Лукас', 'Американский кинорежиссёр, сценарист, продюсер, монтажёр, оператор, предприниматель и филантроп. Основатель кинокомпаний American Zoetrope (совместно с Фрэнсисом Фордом Копполой), Lucasfilm Ltd, ILM, Pixar. Наиболее известен как создатель научно-фантастической саги «Звёздные войны» и серии приключенческих фильмов об Индиане Джонсе.', '1944-05-14'),
 	(18, 'Фернанду Мейреллиш', 'Бразильский кинорежиссёр и сценарист, номинант на премию «Оскар».', '1955-11-09'),
 	(19, 'Джонатан Демми', 'Американский кинорежиссёр, сценарист и продюсер.', '1944-02-22'),
 	(20, 'Франк Капра', 'Американский кинорежиссёр и продюсер итальянского происхождения, мастер бурлескной комедии, лауреат премии «Оскар» (1935, 1937, 1939). Помимо режиссуры, Капра активно работал в киноиндустрии, занимаясь различными политическими и общественными делами.', '1897-05-18'),
	(21, 'Роберто Бениньи', 'Итальянский актёр, режиссёр, сценарист и продюсер, лауреат премий «Оскар» и BAFTA.', '1952-10-27'),
 	(22, 'Брайан Сингер', 'Американский режиссёр, продюсер и сценарист.', '1965-09-17'),
 	(23, 'Люк Бессон', 'Французский кинорежиссёр, сценарист, продюсер и писатель.', '1959-03-18'),
 	(24, 'Хаяо Миядзаки', 'Японский режиссёр-аниматор, продюсер, сценарист, писатель и мангака. Он родился в семье владельца авиационной фабрики, с детства увлекся рисованием манги и анимацией. В 1964 году он познакомился с Исао Такахатой, совместно с которым впоследствии основал анимационную студию Studio Ghibli. На протяжении длительного времени они вместе работали над множеством художественных произведений.', '1941-01-05'),
 	(25, 'Тони Кэй', 'Британский кинорежиссёр, который снимал художественные и документальные фильмы, музыкальные клипы и рекламу.', '1952-07-08'),
 	(26, 'Альфред Хичкок', 'Британский и американский кинорежиссёр, продюсер, сценарист. До 1939 года работал в Великобритании, затем — в США. Хичкок — кинорежиссёр, творчество которого прежде всего связано с жанром «триллер» (от англ. thrill — «трепет») и с понятием «саспенс» (от англ. suspense — «напряжение»). Хичкок умел с непревзойдённым искусством создавать в своих фильмах атмосферу тревожной неопределённости и напряжённого ожидания.', '1899-08-13'),
 	(27, 'Майкл Кёртис', 'Американский кинорежиссёр, который снял более 160 фильмов, в том числе такие успешные, как «Касабланка» (1942) и «Милдред Пирс» (1945).', '1888-12-24'),
 	(28, 'Чарли Чаплин', 'Американский и английский киноактёр, сценарист, композитор, кинорежиссёр, продюсер и монтажёр, универсальный мастер кинематографа, создатель одного из самых знаменитых образов мирового кино — образа бродяги Чарли, появившегося в короткометражных комедиях, поставленных на поток в 1910-е годы на киностудии Кистоун. Чаплин активно использовал приёмы пантомимы и буффонады, хотя начиная с 1920-х годов в его творчестве стали проходить значительно более серьёзные социальные темы, чем это было в раннем периоде короткометражного кино.', '1889-04-16'),
 	(29, 'Оливье Накаш', 'Французский кинорежиссёр, сценарист и актёр.', '1973-04-14'),
 	(30, 'Эрик Толедано', 'Французский режиссёр и сценарист.', '1971-07-03'),
 	(31, 'Роман Полански', 'Польско-французский актёр кино и озвучивания, кинорежиссёр, сценарист и кинопродюсер, выросший в Польше и работавший преимущественно в Великобритании (1963—1967), США (1968—1976) и Франции (после 1976). Обладатель наград: «Золотой пальмовой ветви» в Каннах, «Золотого медведя» Берлинского кинофестиваля, «Золотого льва» Венецианского фестиваля за достижения в течение карьеры, «Оскара», «Сезара», «Золотого глобуса» и премии BAFTA за лучшую режиссуру, а также «Феликса» за лучший фильм, режиссуру и вклад в развитие кино.', '1933-08-18'),
 	(32, 'Джеймс Кэмерон', 'Канадский кинорежиссёр, сценарист, продюсер, монтажёр, океанограф. Один из самых успешных режиссёров в истории кино: восемь его самых популярных картин собрали в прокате более 5,5 млрд долларов. Снял два из самых кассовых художественных фильма в истории кинематографа: «Титаник» (1997), который получил 11 премий «Оскар» (в том числе лично Кэмерону — за лучший фильм, лучшие режиссуру и монтаж) и «Аватар», успех которого привёл к буму 3D-фильмов в начале 2010-х годов.', '1954-08-16'),
 	(33, 'Дэмьен Шазелл', 'Американский независимый режиссёр и сценарист, более всего известный благодаря фильмам «Одержимость» и «Ла-Ла Ленд», за последний из которых стал лауреатом «Оскара» за лучшую режиссуру (самый молодой лауреат в этой номинации за всю историю премии).', '1985-01-19'),
 	(34, 'Ридли Скотт', 'Британский кинорежиссёр и продюсер. Создатель многих прославленных исторических и фантастических фильмов, таких как «Чужой», «Гладиатор», «Царство небесное», «Бегущий по лезвию», «Гангстер», «Совокупность лжи», «Чёрный ястреб», «Марсианин». Старший брат режиссёра и продюсера Тони Скотта. Обладатель премии Каннского кинофестиваля за лучший дебют, обладатель и пятикратный номинант на премию BAFTA, четырёхкратный номинант на премии «Оскар» и «Золотой глобус».', '1937-11-30'),
 	(35, 'Роджер Аллерс', 'Американский кинорежиссёр, сценарист и художник, наиболее известен как режиссёр мультфильма Король Лев', '1949-06-29'),
 	(36, 'Роб Минкофф', 'Американский кинорежиссёр, наиболее известный по своей работе над анимационным фильмом «Король Лев» (вместе с Роджером Аллерсом).', '1962-08-11'),
 	(37, 'Билли Уайлдер', 'Американский кинорежиссёр и сценарист, в продолжение полувека снявший более шестидесяти фильмов и удостоенный семи наград американской киноакадемии, из них шесть Оскаров (1946, 1951, 1961) и один — Приз памяти Ирвинга Дж. Талберга — «за высокое качество общего творческого вклада в киноискусстве» (1988).', '1906-06-22'),
 	(38, 'Стэнли Кубрик', 'Американский и британский кинорежиссёр, фотограф и продюсер, один из самых влиятельных кинематографистов второй половины XX столетия. Фильмы Кубрика, в основном экранизации, сняты с большим техническим мастерством. К особенностям его режиссёрского почерка относят специфическое использование крупных планов, необычного панорамирования, наплыва, а также непривычное употребление популярной и классической музыки. Кубрик часто изображает одиночество человека, его изоляцию от окружающей действительности в собственном, им самим придуманном мире.', '1928-07-26'),
 	(39, 'Джузеппе Торнаторе', 'Итальянский кинорежиссёр, сценарист, продюсер и монтажёр, известный не только как создатель поэтических фильмов, пользовавшихся значительным успехом у итальянских зрителей и за рубежом, но и благодаря своей гражданской позиции. В дополнение к «Оскару» за лучший фильм на иностранном языке и «Золотому глобусу» за фильм "Новый кинотеатр «Парадизо», Торнаторе был удостоен четырех Давид ди Донателло как лучший итальянский режиссер: в 1996 году за фильм «Фабрика звёзд»; в 1999 году за «Легенда о пианисте»; в 2007 году за «Незнакомка» и в 2013 году за «Лучшее предложение»', '1956-05-27'),
 	(40, 'Флориан Хенкель фон Доннерсмарк', 'Немецкий кинорежиссёр, продюсер, сценарист. Наиболее известен по своим фильмам «Жизнь других» («Оскар» за лучший фильм на иностранном языке) и «Турист».', '1973-05-02'),
 	(41, 'Исао Такахата', 'Японский режиссёр анимации, сценарист и продюсер.', '1935-10-29'),
 	(42, 'Эндрю Стэнтон', 'Американский кинорежиссёр, продюсер, художник-аниматор и сценарист. Двукратный лауреат премии «Оскар» за мультфильмы «В поисках Немо» и «ВАЛЛ-И».', '1965-12-03'),
 	(43, 'Сэм Мендес', 'Английский режиссёр театра и кино, продюсер и драматург. Обладатель премии «Оскар» за лучшую режиссуру — является одним из шести режиссёров в истории мирового кинематографа, получивших эту награду за дебютный фильм. Командор ордена Британской империи (CBE, 2000), рыцарь-бакалавр (2020).', '1965-08-01'),
 	(44, 'Пак Чхан Ук', 'Южнокорейский кинорежиссёр, сценарист, кинопродюсер и бывший кинокритик. Неоднократный лауреат как корейских, так и международных кинонаград, в числе которых Гран-при («Олдбой») и Приз жюри («Жажда») Каннского кинофестиваля, «Малый Золотой лев» Венецианского кинофестиваля («Сочувствие госпоже Месть»), «Золотой медведь» Берлинского кинофестиваля за лучший короткометражный фильм («Ночная рыбалка»).', '1963-08-23'),
 	(45, 'Вольфганг Петерсен', 'Немецкий кинорежиссёр, сценарист и продюсер.', '1941-03-14'),
 	(46, 'Орсон Уэллс', 'Американский кинорежиссёр, актёр, сценарист, который работал в театре, на радио и в кино. Его фильмы отмечены изобретательностью в технических решениях. Наиболее примечательные картины режиссёра — «Гражданин Кейн», «Печать зла», «Процесс» и «Леди из Шанхая». Уэллс занимает 16-ю строку в списке Американского института киноискусства среди величайших мужчин-легенд экрана классического голливудского кино.', '1915-05-06'),
 	(47, 'Нитеш Тивари', 'Индийский режиссер, продюсер и сценарист Болливудских фильмов.', '1959-09-19'),
 	(48, 'Ричард Маркуанд', 'Уэльский кинорежиссёр и продюсер. Наиболее известен как режиссёр фильма «Звёздные войны. Эпизод VI: Возвращение джедая».', '1937-09-22'),
 	(49, 'Мел Гибсон', 'Австралийский и американский актёр кино и телевидения, кинорежиссёр, сценарист и продюсер. Лауреат двух премий «Оскар» и премии «Золотой глобус» (1996) за режиссуру драмы «Храброе сердце». В 2017 году был номинирован на премии «Оскар» и «Золотой глобус» за режиссуру военной драмы «По соображениям совести». Почётный офицер ордена Австралии.', '1956-01-03'),
 	(50, 'Фриц Ланг', 'Немецкий кинорежиссёр, с 1934 года живший и работавший в США. Один из величайших представителей немецкого экспрессионизма, Ланг снял самый крупнобюджетный фильм в истории немого кино («Метрополис», 1927) и предвосхитил эстетику американского нуара («Город ищет убийцу», 1931). Фриц Ланг известен также историями о преступных психиатрах-гипнотизёрах Калигари (работа над сценарием) и Мабузе (режиссура всей трилогии).', '1890-12-05'),
 	(51, 'Даррен Аронофски', 'Американский кинорежиссёр, сценарист и продюсер.', '1969-02-12'),
 	(52, 'Жан-Пьер Жене', 'Французский кинорежиссёр и сценарист.', '1953-09-03'),
 	(53, 'Аамир Хан', 'Индийский актёр, продюсер, режиссёр, сценарист и телеведущий. Снимается в фильмах на хинди и зарекомендовал себя как один из самых популярных и влиятельных актёров индийского кино. Обладатель ряда номинаций и кинопремий, в том числе Filmfare Awards и Национальной кинопремии Индии. Был удостоен правительственных наград Падма Шри в 2003 году и Падма Бхушан в 2010.', '1965-03-04'),
 	(54, 'Макото Синкай', 'Японский режиссёр, аниматор и сэйю, родился в префектуре Нагано в 1973 году. Фильм Синкая «Твоё имя» (2016) стал самым кассовым аниме в мире за всю историю до 2018 года. Макото Синкай изучал японскую литературу в университете. Он хотел создавать мангу, аниме и ранобэ, ещё учась в средней школе. Его любимое аниме — «Небесный замок Лапута» Хаяо Миядзаки.', '1973-02-09'),
 	(55, 'Дэвид Лин', 'Британский кинорежиссёр, сценарист и монтажёр, широко признанный мастер мирового кинематографа. Вошёл в десятку лучших режиссёров мирового кино по результатам опроса современных режиссёров Британским институтом киноискусства.', '1908-03-25'),
 	(56, 'Мишель Гондри', 'Французский кинорежиссёр, сценарист и клипмейкер. Лауреат премии «Оскар» за лучший оригинальный сценарий по итогам 2004 года.', '1963-05-08'),
 	(57, 'Роберт Маллиган', 'Американский режиссёр кино и телевидения, сценарист и продюсер, наиболее известный работой над фильмами «Убить пересмешника» (1962), «Лето 42-го» (1971), «Другое» (1972), «В это же время, в следующем году» (1978) и «Человек на Луне» (1991). В 1960-е годы сотрудничал с продюсером Аланом Пакулой.', '1925-08-23'),
 	(58, 'Стэнли Донен', 'Американский кинорежиссёр, продюсер и хореограф, получивший на родине титул «короля голливудских мюзиклов».', '1924-04-13'),
 	(59, 'Джин Келли', 'Американский киноактёр, кинорежиссёр, сценарист, продюсер, хореограф и певец. Обладатель внеконкурсного почётного «Оскара» 1952 года со следующей формулировкой своих заслуг — «в знак признания его универсального таланта актёра, певца, режиссёра и танцовщика и особенно за его блестящие достижения в искусстве кинохореографии». Известен прежде всего своей ролью в знаменитом мюзикле «Поющие под дождём», хотя он был звездой Голливуда в 1940-е и 1950-е годы, создав за это время десятки замечательных фильмов, вошедших в золотой фонд мирового кинематографа.', '1912-08-23'),
 	(60, 'Ли Анкрич', 'Американский кинорежиссёр и монтажёр, лауреат премий «Оскар» (2010) и «Золотой глобус». Постоянный сотрудник компании Pixar, в которой начал работать в 1994 году.', '1967-08-08'),
 	(61, 'Джордж Рой Хилл', 'Американский кинорежиссёр.', '1921-12-20'),
 	(62, 'Джон Лассетер', 'Американский аниматор и режиссёр (CCO) студий Pixar и Walt Disney Animation Studios, один из основателей студии Pixar. Является главным творческим советником Walt Disney Imagineering. Обладатель премии «Оскар».', '1957-01-12'),
 	(63, 'Раджкумар Хирани', 'Индийский режиссёр. Начав снимать фильмы в сорок лет, снял (на 2016 год) четыре фильма, имеющих в Индии культовый статус, за которые получил 64 кинопремии, в том числе 11 премий Filmfare Awards и 4 Национальных кинопремии.', '1962-11-20'),
 	(64, 'Витторио де Сика', 'Итальянский режиссёр и актёр, одна из ключевых фигур итальянского неореализма. Четыре фильма режиссёра были удостоены премии «Оскар»: «Шуша» и «Похитители велосипедов» получили почётные премии.', '1901-07-07'),
 	(65, 'Гай Ричи', 'Британский кинорежиссёр, сценарист, продюсер. Наиболее известные фильмы — «Карты, деньги, два ствола», «Большой куш», «Револьвер», «Рок-н-рольщик», «Агенты А.Н.К.Л.», «Меч короля Артура» и дилогия «Шерлок Холмс».', '1968-09-10');
