 INSERT into film_critic (id, full_name, description) values
	(1, 'Алексий Уминский', 'Cвященнослужитель Русской православной церкви (протоиерей), настоятель храма Святой Троицы в Хохлах, публицист, телеведущий, автор многочисленных публикаций на тему христианской педагогики'),
	(2, 'Стивен Кинг', 'Американский писатель, работающий в разнообразных жанрах, включая ужасы, триллер, фантастику, фэнтези, мистику, драму; получил прозвище «Король ужасов». Продано более 350 миллионов экземпляров его книг, по которым был снят ряд художественных фильмов, телевизионных постановок, а также нарисованы комиксы. Кинг опубликовал 56 романов, в том числе 7 под псевдонимом Ричард Бахман, и 5 научно-популярных книг.'),
	(3, 'Дэйв Кер', 'Видный американский кинокритик, регулярно публиковавшийся в 1974-2013 годах. В настоящее время занимается сохранением и популяризацией американского кинонаследия первых двух третей XX века.'),
	(4, 'Альберто Моравиа', 'Итальянский писатель, новеллист и журналист.'),
	(5, 'Роджер Эберт', 'Американский кинокритик и телеведущий. Лауреат Пулитцеровской премии 1975 года. Автор более чем пятнадцати книг.'),
	(6, 'Энтони Скотт', 'Американский кинокритик, ведущий кинокритик газеты The New York Times, внучатый племянник актёра Илая Уоллака.'),
	(7, 'Винсент Кэнби', 'Американский критик, на протяжении 35 лет рецензировавший фильмы и спектакли в The New York Times'),
	(8, 'Гленн Хетт', 'Абозреватель «Slant Magazine».'),
	(9, 'Джосс Уидон', 'Американский режиссёр, сценарист, актёр и продюсер, автор комиксов. Наибольшую известность получил в качестве создателя сериала «Баффи — истребительница вампиров». Среди других его известных работ — сериалы «Ангел», «Светлячок», «Кукольный дом», «Агенты „Щ. И. Т.“», фильмы «Миссия „Серенити“» (2005), «Мстители» (2012) и «Мстители: Эра Альтрона» (2015).'),
	(10, 'Босли Краузер', 'Американский кинокритик, который в военные и послевоенные годы (1940—1967) руководил киноразделом влиятельнейшей газеты The New York Times.'),
	(11, 'Имре Кертес', 'Венгерский писатель, лауреат Нобелевской премии по литературе за 2002 год, которая была вручена «за творчество, в котором хрупкость личности противопоставлена варварскому деспотизму истории». В 1944—1945 — узник концлагерей Освенцим и Бухенвальд. Этот опыт лежит в основе всего написанного Кертесом позже.'),
	(12, 'Дерек Эллей', 'Американский кинокритик, резидент еженедельника Variety'),
	(13, 'Джэнет Мэслин', 'Американская журналистка, известная как фильмограф и литературовед для The New York Times.'),
	(14, 'Нил Деграсс Тайсон', 'Американский астрофизик, доктор философии по физике, писатель, популяризатор науки.'),
	(15, 'Питер Богданович', 'Американский кинорежиссёр, сценарист, актёр, кинопродюсер, кинооператор, кинокритик и писатель.'),
	(16, 'Жорж Садуль', 'Французский историк, теоретик и критик кино.'),
	(17, 'Юрий Ханютин', 'Советский киновед, кинокритик, сценарист. Кандидат искусствоведения.'),
	(18, 'Стивен Хантер', 'Американский писатель и кинокритик.'),
	(19, 'Роман Волобуев', 'Российский журналист, кинокритик, сценарист, режиссёр, продюсер, монтажёр.'),
	(20, 'Алекс Экслер', 'Российский писатель, сатирик, блогер и кинокритик, менеджер различных сетевых проектов, радиоведущий'),
	(21, 'Эндрю Саррис', 'Американский кинокритик и теоретик кино. Обозреватель нью-йоркского еженедельника Village Voice, традиционный оппонент Полин Кейл.'),
	(22, 'Майкл Аткинсон', 'Американский кинокритик, писатель и поэт.'),
	(23, 'Полин Кейл', 'Американская журналистка и кинокритик, чьи «остроумные, саркастичные, пристрастные и резкофокусные» рецензии, зачастую противоречившие мнениям современников, сделали её, возможно, самым влиятельным из когда-либо живших кинокритиков.'),
	(24, 'Марк Кермод', 'Английский кинокритик и член Британской академии кино и телевизионных искусств.'),
	(25, 'Марлен Хуциев', 'Советский и российский кинорежиссёр, сценарист, актёр и педагог грузинского происхождения. Народный артист СССР (1986). Лауреат Государственной премии РФ (1993). Почётный член Российской академии художеств.');
  INSERT into film_critic_review (film_id, film_critic_id, review) values
 	(2, 1, 'И такие замечательные фильмы, как «Однажды в Америке» или «Крёстный отец», по сути, продолжают тему романтизации зла. Ведь как бы оно ни выглядело на экране, очевидно, что реальность совсем иная. И не дай Бог в настоящей жизни попасть в одну из тех ситуаций, что показаны в этих фильмах. Никакой романтики в изрешечённых пулями телах родных и близких нет.'),
	(4, 2, '«Темный рыцарь»-лучший фильм о супергерое.'),
	(9, 7, 'Хороший — относится к работе оператора — профессиональное сочетание цветов и композиции, внимание форме и текстуре объектов, что можно достичь съёмками на натуре, а не в студии. Плохой — подходящее слово для деревянной актёрской игры и пристрастию Леоне к смятым ценностям и натянутым вероятностям комиксов. Злой — ненасытный аппетит к избиениям, выпусканию кишок и изувечиванию, в сочетании с крупными планами разбитых лиц и звуковыми эффектами предсмертных хрипов.'),
	(9, 4, '«Если американский вестерн — миф, то здесь миф из мифа».'),
	(9, 5, 'В своей второй рецензии (2003 год) Роджер Эберт сетовал на то, что в первый раз (1968 год) не разглядел всех достоинств картины, будучи тогда не очень опытным критиком. Эберт поставил высшую оценку и включил картину в число лучших работ в истории кинематографа'),
	(14, 5, '«В наше время фильмы частенько кажутся извлечёнными из мусорной корзины: сиквелы, ремейки, сериалы. Создатели „Начала“ проделали более сложную работу. Фильм представляет собой совершенно оригинальное творение, начатое с белого листа»'),
	(14, 6, '«Большую часть времени одни мужики с пушками преследуют других: в машинах, по залитым дождём улицам Лос-Анджелеса; пешком, по коридорам элегантно-старомодного отеля; на лыжах и снегоходах, по ледяному альпийскому пейзажу, который, вероятно, только что покинул Джеймс Бонд»'),
	(16, 7, '«Картина не даёт ни на йоту усомниться в безупречном вкусе режиссёра».'),
	(16, 5, 'Поставил картине три звезды из четырёх по своей шкале, оставшись не совсем удовлетворённым некоторыми сценами. Поездку на рыбалку он нашёл слишком надуманной и символичной, выдающей замысел режиссёра не запирать всю интригу в четыре стены. Самоубийство Билли Биббита также выпадает из общего настроения и ритма.'),
	(16, 8, '«Эмоциональное опустошение не отпускает вас даже спустя 35 лет после первого просмотра. В фильме бездна обаяния и харизмы, но головокружительная концовка снова и снова сбивает зрителя с ног. Наблюдать энергию и энтузиазм персонажа Николсона высшее удовольствие. Вновь становиться свидетелем того, как расстаётся он с жизненной силой, снова и снова, становится величайшей трагедией.»'),
	(17, 7, '«"Славные парни" выглядят и звучат так, что ты веришь, что всё это абсолютно достоверно»'),
	(18, 5, 'Поставил фильму три звезды из четырёх возможных. Его разочаровало то, что «фильм начинается с пересмотра природы реальности, а заканчивается перестрелкой. Мы хотим прыжка воображения, а не очередной кульминации с непременными автоматными очередями. Мне пришлось повидать десятки, если не сотни подобных упражнений в насилии, которые эксплуатируют одни и те же давно затёртые идеи».'),
	(18, 9, '«Мой номер один. Он смотрится на том уровне, на котором ты хочешь его смотреть»'),
	(22, 5, '«Фильм, в отличие от большинства голливудских триллеров или хорроров, где сцены насилия и ужасов показаны для привлечения внимания зрителей, «Семь» снят так, чтобы вызвать чувство омерзения»'),
	(24, 10, 'Слабая сторона фильма заключается в его сентиментальном характере и «иллюзорности такой картины мира»: «Эти милые люди очаровательны, городок — привлекателен, а способ решать проблемы жизнеутверждающ и прост. Но почему-то всё это скорее напоминает театральные подмостки, чем реальную жизнь»'),
	(25, 11, '«Те, кто считает фильм комедией, а не трагедией, упустили смысл фильма. Он привлекает внимание к тому, что он называет «конформизмом Холокоста» в кино».'),
	(25, 5, '«В Каннах он оскорбил некоторых критиков использованием юмора в отношении Холокоста. Что может быть наиболее оскорбительным для всех, так это его обход политики в пользу простой человеческой изобретательности».'),
	(26, 5, 'Роджер Эберт, признавая, что запутался в сюжетных перипетиях и был обескуражен неожиданной развязкой, разнёс фильм в пух и прах; впоследствии он включил его в число своих самых нелюбимых фильмов'),
	(29, 5, 'Роджер Эберт дал фильму четыре полных звезды, хваля сам фильм и режиссуру Миядзаки. Эберт также отметил, что «Унесённые призраками» был одним из лучших фильмов года.'),
	(29, 12, '«"Унесённые призраками" могут понравиться как детям, так и взрослым, также похвалил анимацию и музыку».'),
	(30, 5, 'В рецензии, опубликованной в таблоиде Chicago Sun-Times, поставил фильму три из четырёх звёзд, назвав его «интересным, иногда убедительным и содержащим больше смысла, чем любой подобный американский фильм, как, например, „Делай, как надо!“».'),
	(30, 13, 'Джэнет Мэслин отметила хорошую работу Кэя и Нортона, но подчеркнула, что некоторые сцены фильма получились слишком шокирующими и излишне жестокими. Она добавила, что фильм станет «настоящим хитом для людей с крепкими нервами»'),
	(32, 14, 'Нил Деграсс Тайсон похвалил фильм, заметив, что теория относительности ещё никогда так хорошо не была представлена на экране, и подверг критике только идею искать новый дом возле чёрной дыры.'),
	(33, 15, '«Возможно, самый визуальный, наиболее кинематографичный фильм, когда-либо созданный».'),
	(35, 5, '«Главные герои проделывают по ходу фильма внутреннюю эволюцию — они приходят к мысли о необходимости принести в жертву победе нечто весьма дорогое для них».'),
	(35, 16, '«Классический гангстерский фильм, герои которого заменены деятелями Сопротивления, одурачившими в Марокко гестаповцев при помощи вишиста, ведущего двойную игру».'),
	(44, 5, '«…Возможно, стоит задуматься, не следует ли Спилберг примеру великих студийных боссов прошлого, отличавшихся умелым выбором нужного режиссёра для нужного проекта. В этом проекте благодаря такому сочетанию всё вышло с шармом, умом и массой смеха».'),
	(45, 6, '«Картина не гениальная, но она «слышала о том, что такое гениальность»».'),
	(50, 3, '«Фильм Копполы показывает, что его истинное призвание — быть художником или фотографом, ибо фабула занимает его только как повод для демонстрации обворожительных «картинок»».'),
	(50, 5, 'Роджер Эберт, объявил «Апокалипсис сегодня» лучшим фильмом 1979 года и впоследствии добавил его в свой список великих фильмов на том основании, что «он продвигается гораздо дальше, чем все остальные, в тёмные уголки человеческой души»[14]. По словам Эберта, это «не столько фильм о войне, сколько о том, как война вытаскивает на поверхность такую правду о людях, которую они предпочли бы никогда не знать».'),
	(54, 17, '«Фильм — сатира на Пентагон, на армию, военное и гражданское руководство. Изображение тупости, чванства, упрямства, сумасшествия и непредусмотрительности, которые приводят человечество к гибели», тем не менее добавляя: «Надо сказать, что … Кубрик не смог избежать булавочных уколов в адрес советских деятелей и советской политики, изменил своей антиимпериалистической позиции».'),
	(56, 5, '«Сильный, но тихий фильм, состоящим из скрытых мыслей и секретных желаний».'),
	(56, 6, '««Жизнь» сделан довольно добротно, и добавил, что «саспенс создаётся не только благодаря структуре сцен и темпу фильма, но также и за счёт идеи о том, что даже в репрессивном обществе люди обременены доброй волей. Вы никогда не знаете, смотря одну сцену за другой, какое решение примет персонаж буквально через мгновение».'),
	(57, 5, 'По мнению американского критика Роджера Эберта фильм принадлежит к списку лучших фильмов о войне.'),
	(63, 5, '«Фильм начинается медленно, сумбурно, новых персонажей слишком много, но при этом кульминация у фильма впечатляющая».'),
	(64, 5, 'Роджер Эберт из газеты Chicago Sun-Times охарактеризовал «Принцессу Мононоке» как большое достижение и замечательный пример одного из лучших фильмов года.'),
	(64, 18, 'Хоть фильм и является захватывающим и красочным, но одновременно он бессмысленный и долгий. Очень долгий.'),
	(66, 19, '«„Олдбой“ обладает необъяснимой сверхъестественной силой. От большинства сегодняшних фильмов, включая и своих лютых собратьев по „актуальному азиатскому кинематографу“, он отличается как поэзия отличается от прозы, как спирт от дистиллированной воды, как, в конце концов, жизнь от смерти».'),
	(66, 20, '«„Олдбой“ — один из самых сильных фильмов, который я увидел за последние несколько лет. Фильм вовсе не о мести как таковой. Главная тема — преступление и соответствующее наказание». «Чхве Мин Сик играет Дэ Су совершенно потрясающе. Почти никаких внешних эффектов, криков и воплей — только глаза, в которых виднеется весь перенесённый ужас и одновременно страх узнать то, чего он так добивается.'),
	(68, 3, '«Сила фильма как раз в том, что он не замыкается в оболочке высокого искусства, а занимает и развлекает зрителя не хуже Голливуда. Сцена за сценой 4-часового фильма построены так, чтобы производить на зрителя максимальный эмоциональный эффект: каждый жест непосредствен и вместе с тем отпечатан в вечности».'),
	(70, 10, '«Близок к тому, чтобы называться самой блестящей картиной из когда-либо снятых в Голливуде».'),
	(70, 16, '«Технические новшества фильма изобретены не для того, чтобы придать ему жизни. Встречаются примеры великолепной операторской работы... Но слишком часто возникает впечатление, что изображение ставится превыше всего; нас заваливают перегруженными кадрами, неестественными и вымученными. То же бывает и в романах, когда на первый план выдвигается стиль, а персонажи настолько блёклы, что забываются через мгновение».'),
	(74, 7, 'Известный американский критик Винсент Кэнби, которому понравился первый фильм трилогии и не понравился второй, сказал, что третий фильм получился худшим в серии.'),
	(76, 7, 'Винсенту Кэнби из газеты The New York Times понравился актёрский состав и выбор нелинейной структуры повествования. Похвалив режиссуру Тарантино, он в качестве положительной черты отметил нечастое использование крупных планов в фильме.'),
	(76, 5, '«Мне понравилось, что я видел, но мне хотелось большего… Он был снят с небольшим бюджетом. Но та часть, с которой нужно поработать, не стоила денег. Это сценарий. Создав своих героев и набросав сюжетную линию, Тарантино не слишком многого от них требует, кроме того, чтобы они слишком много болтали, особенно тогда, когда они должны быть без сознания от шока и потери крови».'),
	(82, 7, '«Блестящая и опасная работа».'),
	(82, 5, '«Фильм лишь маскируется под пророчество в стиле Оруэлла, а сам режиссёр только делает вид, что выступает против тоталитаризма и принудительного контроля над разумом».'),
	(82, 21, '«Скучный и претенциозный фильм».'),
	(86, 5, 'Роджер Эберт считал «Таксиста» одним из наиболее сильных фильмов в истории кинематографа. Он толковал его как рассказ о неискоренимой потребности человека в общении с другими людьми и о том психическом надломе, который неизбежен при фатальной неспособности к коммуникации. В этом смысле он считал характерной знаменитую сцену, когда герой Де Ниро беспрестанно спрашивает у своего отражения в зеркале: «Ты со мной говоришь?».'),
	(86, 22, '«Герой Де Ниро — своего рода всечеловек, наиболее американский из киногероев, бесправная жертва амбиций нации, приучившей его к бессмысленности смерти на полях далёкой войны, затерянная на инфернальных улицах самого свободного города мирам».'),
	(88, 5, '«Радиционные для сценариев Кауфмана зигзаги повествования в данном случае вовсе не произвольны, а искусно закручены вокруг «эмоционального центра»».'),
	(88, 21, 'Эндрю Саррис был разочарован отсутствием «химии» между исполнителями главных ролей. Ему показалось, что Уинслет играет роль, предназначенную для обычно бойкого Кэрри, а Кэрри — роль, предназначенную для Уинслет, долгое время специализировавшейся на ролях недотрог.'),
	(91, 21, 'Эндрю Саррис с журнала Village Voice назвал картину «одним из самых мрачных фильмов, которые я когда-либо видел в своей жизни… 2001 год — это катастрофа, потому что она слишком абстрактна, чтобы ее резюмировать». Саррис после второго просмотра фильма изменил свое мнение и заявил, что «2001 год — действительно масштабная работа талантливого художника».'),
	(91, 23, '«Монументально невообразимый фильм».'),
	(93, 6, '«15-летняя эпопея о похождениях дурацкого пластмассового утильсырья оборачивается долгой меланхоличной медитацией об утраченном, о мимолётности бытия и об этой благородной, упрямой и глупой штуке под названием любовь».'),
	(93, 24, '«Лучшая кинотрилогия всех времён».'),
	(96, 12, '«„3-м идиотам“ требуется время, чтобы наметить стратегию, но это эмоционально окупается во второй его половине».'),
	(97, 10, '«Полный отчаяния сюжет раскрывает разложение послевоенного Рима. Основанный на простой драме фильм обостряет восприятие общечеловеческих фундаментальных ценностей. Трагедия одинокого маленького человека в безжалостном мире призывает к его защите и успокоению».'),
	(97, 25, '«Картина произвела оглушительное впечатление. Что мы знали об этой, чужой для нас стране? Что итальянцы были нашими врагами, фашистами […] А в этом фильме увидели обыкновенных людей, которые бедствуют, ищут работу, любят, надеются. «Похитители велосипедов» словно вернули нас к нашему кинематографу 20-30-х годов. Так что утверждение, что неореализм родился под влиянием нашего кино, достаточно справедливо».');
